import React, { useState } from "react";

const TableWithActions = () => {
  // State to store table rows and edit data
  const [rows, setRows] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [newRow, setNewRow] = useState({ title: "", description: "" });

  // Function to handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewRow((prevRow) => ({
      ...prevRow,
      [name]: value,
    }));
  };

  // Add a new row to the table
  const handleAddRow = () => {
    if (newRow.title && newRow.description) {
      setRows([...rows, { ...newRow }]);
      setNewRow({ title: "", description: "" }); // Clear input fields after adding
    } else {
      alert("Please fill in all fields.");
    }
  };

  // Function to handle editing a row
  const handleEditRow = (index) => {
    const row = rows[index];
    setEditIndex(index);
    setNewRow({ title: row.title, description: row.description });
  };

  // Save edited row data
  const handleSaveEdit = () => {
    if (newRow.title && newRow.description) {
      const updatedRows = [...rows];
      updatedRows[editIndex] = newRow; // Update the specific row
      setRows(updatedRows);
      setEditIndex(null); // Reset edit mode
      setNewRow({ title: "", description: "" }); // Clear the input fields
    } else {
      alert("Please fill in all fields.");
    }
  };

  // Cancel editing and reset to original row data
  const handleCancelEdit = () => {
    setEditIndex(null);
    setNewRow({ title: "", description: "" });
  };

  // Delete a row
  const handleDeleteRow = (index) => {
    const updatedRows = rows.filter((_, i) => i !== index);
    setRows(updatedRows);
  };

  return (
    <div className="min-h-screen bg-gray-200 p-6">
      <h2 className="text-2xl font-bold mb-6">Table with Add, Edit, and Delete</h2>

      {/* Form to Add/Edit Row */}
      <div className="mb-6">
        <input
          type="text"
          name="title"
          value={newRow.title}
          onChange={handleInputChange}
          placeholder="Title"
          className="border border-gray-400 p-2 rounded-md mr-2"
        />
        <input
          type="text"
          name="description"
          value={newRow.description}
          onChange={handleInputChange}
          placeholder="Description"
          className="border border-gray-400 p-2 rounded-md mr-2"
        />
        {editIndex === null ? (
          <button
            onClick={handleAddRow}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
          >
            Add Row
          </button>
        ) : (
          <div>
            <button
              onClick={handleSaveEdit}
              className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600 mr-2"
            >
              Save
            </button>
            <button
              onClick={handleCancelEdit}
              className="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600"
            >
              Cancel
            </button>
          </div>
        )}
      </div>

      {/* Table Display */}
      <table className="w-full border-collapse">
        <thead>
          <tr className="bg-gray-400 text-white">
            <th className="border px-4 py-2">ID</th>
            <th className="border px-4 py-2">Title</th>
            <th className="border px-4 py-2">Description</th>
            <th className="border px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {rows.map((row, index) => (
            <tr key={index} className="bg-gray-100">
              <td className="border px-4 py-2">{index + 1}</td>
              <td className="border px-4 py-2">
                {editIndex === index ? (
                  <input
                    type="text"
                    name="title"
                    value={newRow.title}
                    onChange={handleInputChange}
                    className="border p-2 rounded-md w-full"
                  />
                ) : (
                  row.title
                )}
              </td>
              <td className="border px-4 py-2">
                {editIndex === index ? (
                  <input
                    type="text"
                    name="description"
                    value={newRow.description}
                    onChange={handleInputChange}
                    className="border p-2 rounded-md w-full"
                  />
                ) : (
                  row.description
                )}
              </td>
              <td className="border px-4 py-2">
                {editIndex === index ? (
                  <>
                    <button
                      onClick={handleSaveEdit}
                      className="bg-blue-500 text-white px-2 py-1 rounded-md hover:bg-blue-600 mr-2"
                    >
                      Save
                    </button>
                    <button
                      onClick={handleCancelEdit}
                      className="bg-gray-500 text-white px-2 py-1 rounded-md hover:bg-gray-600"
                    >
                      Cancel
                    </button>
                  </>
                ) : (
                  <>
                    <button
                      onClick={() => handleEditRow(index)}
                      className="bg-yellow-500 text-white px-2 py-1 rounded-md hover:bg-yellow-600 mr-2"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteRow(index)}
                      className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600"
                    >
                      Delete
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TableWithActions;
