import React, { useState } from "react";
import { Link } from "react-router-dom";

const ManagePortfolio = () => {
  const [imageData, setImageData] = useState([
    { id: 1, eventId: 1, title: "Image 1", url: "https://via.placeholder.com/150", description: "Image description" },
    { id: 2, eventId: 2, title: "Image 2", url: "https://via.placeholder.com/150", description: "Image description" },
  ]);

  const [videoData, setVideoData] = useState([
    { id: 1, title: "Video 1", youtubeLink: "https://www.youtube.com/watch?v=xxx" },
    { id: 2, title: "Video 2", youtubeLink: "https://www.youtube.com/watch?v=yyy" },
  ]);

  const [currentImagePage, setCurrentImagePage] = useState(1);
  const [currentVideoPage, setCurrentVideoPage] = useState(1);
  const itemsPerPage = 5;

  const [newImage, setNewImage] = useState({ eventId: "", title: "", url: "" });
  const [newVideo, setNewVideo] = useState({ title: "", youtubeLink: "" });

  const [isEditingImage, setIsEditingImage] = useState(false);
  const [isEditingVideo, setIsEditingVideo] = useState(false);
  const [currentEditingImage, setCurrentEditingImage] = useState(null);
  const [currentEditingVideo, setCurrentEditingVideo] = useState(null);

  const [message, setMessage] = useState("");

  const handleCreateImage = () => {
    if (newImage.eventId && newImage.title && newImage.url) {
      setImageData([...imageData, { id: imageData.length + 1, ...newImage }]);
      setNewImage({ eventId: "", title: "", url: "" });
      setMessage("Image added successfully.");
    } else {
      alert("Please fill all fields for the image.");
    }
  };

  const handleCreateVideo = () => {
    if (newVideo.title && newVideo.youtubeLink) {
      setVideoData([...videoData, { id: videoData.length + 1, ...newVideo }]);
      setNewVideo({ title: "", youtubeLink: "" });
      setMessage("Video added successfully.");
    } else {
      alert("Please fill all fields for the video.");
    }
  };

  const handleDeleteImage = (id) => {
    if (window.confirm("Are you sure you want to delete this image?")) {
      setImageData(imageData.filter((image) => image.id !== id));
      setMessage("Image deleted successfully.");
    }
  };

  const handleDeleteVideo = (id) => {
    if (window.confirm("Are you sure you want to delete this video?")) {
      setVideoData(videoData.filter((video) => video.id !== id));
      setMessage("Video deleted successfully.");
    }
  };

  const handleUpdateImage = (image) => {
    setIsEditingImage(true);
    setCurrentEditingImage({ ...image });
  };

  const handleUpdateVideo = (video) => {
    setIsEditingVideo(true);
    setCurrentEditingVideo({ ...video });
  };

  const saveUpdatedImage = () => {
    setImageData(
      imageData.map((image) =>
        image.id === currentEditingImage.id ? currentEditingImage : image
      )
    );
    setIsEditingImage(false);
    setCurrentEditingImage(null);
    setMessage("Image updated successfully.");
  };

  const saveUpdatedVideo = () => {
    setVideoData(
      videoData.map((video) =>
        video.id === currentEditingVideo.id ? currentEditingVideo : video
      )
    );
    setIsEditingVideo(false);
    setCurrentEditingVideo(null);
    setMessage("Video updated successfully.");
  };

  const indexOfLastImage = currentImagePage * itemsPerPage;
  const indexOfFirstImage = indexOfLastImage - itemsPerPage;
  const currentImages = imageData.slice(indexOfFirstImage, indexOfLastImage);

  const indexOfLastVideo = currentVideoPage * itemsPerPage;
  const indexOfFirstVideo = indexOfLastVideo - itemsPerPage;
  const currentVideos = videoData.slice(indexOfFirstVideo, indexOfLastVideo);

  const paginateImage = (pageNumber) => setCurrentImagePage(pageNumber);
  const paginateVideo = (pageNumber) => setCurrentVideoPage(pageNumber);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <div className="mb-6">
        <Link
          to="/dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Go to Dashboard
        </Link>
      </div>

      <h2 className="text-2xl font-bold text-center mb-6">Manage Portfolio</h2>

      {message && (
        <div className="bg-green-100 text-green-800 p-4 rounded-md mb-4">
          {message}
        </div>
      )}

      <div className="flex gap-6">
        {/* Image Management */}
        <div className="w-1/2 bg-white p-4 rounded-md shadow">
          <h3 className="text-xl font-semibold text-center mb-4">Image Management</h3>
          <div className="mb-4 grid grid-cols-4 gap-2">
            <input
              type="text"
              placeholder="Event ID"
              value={newImage.eventId}
              onChange={(e) => setNewImage({ ...newImage, eventId: e.target.value })}
              className="p-2 rounded-md border border-gray-400"
            />
            <input
              type="text"
              placeholder="Title"
              value={newImage.title}
              onChange={(e) => setNewImage({ ...newImage, title: e.target.value })}
              className="p-2 rounded-md border border-gray-400"
            />
            <input
              type="text"
              placeholder="Image URL"
              value={newImage.url}
              onChange={(e) => setNewImage({ ...newImage, url: e.target.value })}
              className="p-2 rounded-md border border-gray-400"
            />
            <button
              onClick={handleCreateImage}
              className="bg-orange-400 text-white px-4 py-2 rounded-md"
            >
              Add Image
            </button>
          </div>

          <table className="w-full table-auto text-left mt-4">
            <thead className="bg-orange-400 text-white">
              <tr>
                <th className="p-4">ID</th>
                <th className="p-4">Event ID</th>
                <th className="p-4">Title</th>
                <th className="p-4">URL</th>
                <th className="p-4">Actions</th>
              </tr>
            </thead>
            <tbody>
              {currentImages.map((image) => (
                <tr key={image.id}>
                  <td className="p-4">{image.id}</td>
                  <td className="p-4">{image.eventId}</td>
                  <td className="p-4">{image.title}</td>
                  <td className="p-4">{image.url}</td>
                  <td className="p-4">
                    <button
                      onClick={() => handleUpdateImage(image)}
                      className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2"
                    >
                      Update
                    </button>
                    <button
                      onClick={() => handleDeleteImage(image.id)}
                      className="bg-red-500 text-white px-4 py-2 rounded-md"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="flex justify-center mt-4">
            {Array.from(
              { length: Math.ceil(imageData.length / itemsPerPage) },
              (_, i) => (
                <button
                  key={i + 1}
                  onClick={() => paginateImage(i + 1)}
                  className={`px-4 py-2 border ${
                    currentImagePage === i + 1 ? "bg-orange-400 text-white" : "bg-white"
                  }`}
                >
                  {i + 1}
                </button>
              )
            )}
          </div>
        </div>

        {/* Video Management */}
        <div className="w-1/2 bg-white p-4 rounded-md shadow">
          <h3 className="text-xl font-semibold text-center mb-4">Video Management</h3>
          <div className="mb-4 grid grid-cols-3 gap-2">
            <input
              type="text"
              placeholder="Title"
              value={newVideo.title}
              onChange={(e) => setNewVideo({ ...newVideo, title: e.target.value })}
              className="p-2 rounded-md border border-gray-400"
            />
            <input
              type="text"
              placeholder="YouTube Link"
              value={newVideo.youtubeLink}
              onChange={(e) => setNewVideo({ ...newVideo, youtubeLink: e.target.value })}
              className="p-2 rounded-md border border-gray-400"
            />
            <button
              onClick={handleCreateVideo}
              className="bg-orange-400 text-white px-4 py-2 rounded-md"
            >
              Add Video
            </button>
          </div>

          <table className="w-full table-auto text-left mt-4">
            <thead className="bg-orange-400 text-white">
              <tr>
                <th className="p-4">ID</th>
                <th className="p-4">Title</th>
                <th className="p-4">YouTube Link</th>
                <th className="p-4">Actions</th>
              </tr>
            </thead>
            <tbody>
              {currentVideos.map((video) => (
                <tr key={video.id}>
                  <td className="p-4">{video.id}</td>
                  <td className="p-4">{video.title}</td>
                  <td className="p-4">{video.youtubeLink}</td>
                  <td className="p-4">
                    <button
                      onClick={() => handleUpdateVideo(video)}
                      className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2"
                    >
                      Update
                    </button>
                    <button
                      onClick={() => handleDeleteVideo(video.id)}
                      className="bg-red-500 text-white px-4 py-2 rounded-md"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="flex justify-center mt-4">
            {Array.from(
              { length: Math.ceil(videoData.length / itemsPerPage) },
              (_, i) => (
                <button
                  key={i + 1}
                  onClick={() => paginateVideo(i + 1)}
                  className={`px-4 py-2 border ${
                    currentVideoPage === i + 1 ? "bg-orange-400 text-white" : "bg-white"
                  }`}
                >
                  {i + 1}
                </button>
              )
            )}
          </div>
        </div>
      </div>

      {/* Modals */}
      {isEditingImage && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-4 rounded-md">
            <h3 className="text-xl font-semibold mb-4">Update Image</h3>
            <input
              type="text"
              placeholder="Title"
              value={currentEditingImage.title}
              onChange={(e) =>
                setCurrentEditingImage({ ...currentEditingImage, title: e.target.value })
              }
              className="p-2 mb-2 rounded-md border border-gray-400 w-full"
            />
            <input
              type="text"
              placeholder="Image URL"
              value={currentEditingImage.url}
              onChange={(e) =>
                setCurrentEditingImage({ ...currentEditingImage, url: e.target.value })
              }
              className="p-2 mb-4 rounded-md border border-gray-400 w-full"
            />
            <button
              onClick={saveUpdatedImage}
              className="bg-green-500 text-white px-4 py-2 rounded-md mr-2"
            >
              Save
            </button>
            <button
              onClick={() => setIsEditingImage(false)}
              className="bg-gray-500 text-white px-4 py-2 rounded-md"
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {isEditingVideo && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-4 rounded-md">
            <h3 className="text-xl font-semibold mb-4">Update Video</h3>
            <input
              type="text"
              placeholder="Title"
              value={currentEditingVideo.title}
              onChange={(e) =>
                setCurrentEditingVideo({ ...currentEditingVideo, title: e.target.value })
              }
              className="p-2 mb-2 rounded-md border border-gray-400 w-full"
            />
            <input
              type="text"
              placeholder="YouTube Link"
              value={currentEditingVideo.youtubeLink}
              onChange={(e) =>
                setCurrentEditingVideo({ ...currentEditingVideo, youtubeLink: e.target.value })
              }
              className="p-2 mb-4 rounded-md border border-gray-400 w-full"
            />
            <button
              onClick={saveUpdatedVideo}
              className="bg-green-500 text-white px-4 py-2 rounded-md mr-2"
            >
              Save
            </button>
            <button
              onClick={() => setIsEditingVideo(false)}
              className="bg-gray-500 text-white px-4 py-2 rounded-md"
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ManagePortfolio;
