import React, { useState } from "react";
import { Link } from "react-router-dom";

const Home_Page = () => {
  const [searchTerm, setSearchTerm] = useState(""); // State for search input
  const [searchField, setSearchField] = useState("name"); // State for search filter field
  const [currentPage, setCurrentPage] = useState(1); // State for pagination
  const [statusFilter, setStatusFilter] = useState(""); // State for status filter
  const itemsPerPage = 8; // Number of rows per page
  const [data, setData] = useState([
    {
      id: 1,
      name: "John Doe",
      email: "john@example.com",
      phone: "123-456-7890",
      country: "USA",
      message: "Inquiry about the product.",
      status: "Waiting",
    },
    {
      id: 2,
      name: "Jane Smith",
      email: "jane@example.com",
      phone: "987-654-3210",
      country: "Canada",
      message: "Need more details about the service.",
      status: "Finished",
    },
    // Add more items as needed...
  ]);

  const [countryDropdownOpen, setCountryDropdownOpen] = useState(false); // State to toggle country dropdown
  const [statusDropdownOpen, setStatusDropdownOpen] = useState(false); // State to toggle status dropdown

  // Function to handle status changes for a specific item
  const handleStatusChange = (id, newStatus) => {
    setData((prevData) =>
      prevData.map((item) =>
        item.id === id ? { ...item, status: newStatus } : item
      )
    );
  };

  // Function to handle country filter
  const handleCountryFilter = (country) => {
    setSearchTerm(country);
    setSearchField("country");
    setCountryDropdownOpen(false); // Close country dropdown after selection
  };

  // Pagination logic
  const filteredData = data.filter((item) => {
    const matchesSearchTerm = item[searchField]
      .toString()
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    const matchesStatusFilter =
      !statusFilter || item.status === statusFilter;
    return matchesSearchTerm && matchesStatusFilter;
  });

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentData = filteredData.slice(indexOfFirstItem, indexOfLastItem);

  const totalPages = Math.ceil(filteredData.length / itemsPerPage);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      {/* Heading and Search Input */}
      <div className="mb-6 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-black">Manage Booking</h1>
        <input
          type="text"
          placeholder="Search"
          className="w-64 p-2 rounded-md border border-gray-400 focus:outline-none focus:ring-2 focus:ring-orange-400"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Filters */}
      <div className="mb-4 flex justify-start gap-4">
        <div className="relative">
          <button
            onClick={() => setCountryDropdownOpen(!countryDropdownOpen)}
            className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500"
          >
            Filter by Country
          </button>
          {countryDropdownOpen && (
            <div className="absolute bg-white shadow-lg rounded-md mt-2 p-2 border border-gray-300">
              <div
                onClick={() => handleCountryFilter("USA")}
                className="p-2 hover:bg-gray-200 cursor-pointer"
              >
                USA
              </div>
              <div
                onClick={() => handleCountryFilter("Canada")}
                className="p-2 hover:bg-gray-200 cursor-pointer"
              >
                Canada
              </div>
            </div>
          )}
        </div>

        <div className="relative">
          <button
            onClick={() => setStatusDropdownOpen(!statusDropdownOpen)}
            className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500"
          >
            Filter by Status
          </button>
          {statusDropdownOpen && (
            <div className="absolute bg-white shadow-lg rounded-md mt-2 p-2 border border-gray-300">
              <div
                onClick={() => {
                  setStatusFilter("Waiting");
                  setStatusDropdownOpen(false);
                }}
                className="p-2 hover:bg-gray-200 cursor-pointer"
              >
                Waiting
              </div>
              <div
                onClick={() => {
                  setStatusFilter("Finished");
                  setStatusDropdownOpen(false);
                }}
                className="p-2 hover:bg-gray-200 cursor-pointer"
              >
                Finished
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Data Table */}
      <div className="overflow-x-auto bg-white shadow-md rounded-lg">
        <table className="w-full table-auto text-left">
          <thead className="bg-orange-400 text-white">
            <tr>
              <th className="p-4">ID</th>
              <th className="p-4">Name</th>
              <th className="p-4">Email</th>
              <th className="p-4">Phone</th>
              <th className="p-4">Country</th>
              <th className="p-4">Message</th>
              <th className="p-4">Status</th>
            </tr>
          </thead>
          <tbody>
            {currentData.map((item) => (
              <tr key={item.id} className="border-b last:border-none">
                <td className="p-4 text-black">{item.id}</td>
                <td className="p-4 text-black">{item.name}</td>
                <td className="p-4 text-blue-500 underline">
                  <a href={`mailto:${item.email}`}>{item.email}</a>
                </td>
                <td className="p-4 text-blue-500 underline">
                  <a href={`tel:${item.phone}`}>{item.phone}</a>
                </td>
                <td className="p-4 text-black">{item.country}</td>
                <td className="p-4 text-black">{item.message}</td>
                <td className="p-4">
                  <select
                    value={item.status}
                    onChange={(e) => handleStatusChange(item.id, e.target.value)}
                    className={`p-2 rounded-md border border-gray-400 ${
                      item.status === "Finished" ? "bg-green-300" : "bg-gray-300"
                    } focus:outline-none focus:ring-2 focus:ring-orange-400`}
                  >
                    <option value="Waiting">Waiting</option>
                    <option value="Finished">Finished</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      <div className="mt-4 flex justify-end items-center gap-4">
        <button
          className={`px-4 py-2 rounded-md bg-orange-400 text-white hover:bg-orange-500`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
        >
          Previous
        </button>
        <span className="text-black">
          Page {currentPage} of {totalPages}
        </span>
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage((prev) => prev + 1)}
        >
          Next
        </button>
      </div>

      {/* Dashboard Button */}
      <div className="mt-6 flex justify-left">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Dashboard
        </Link>
      </div>
    </div>
  );
};

export default Home_Page;
