import React, { useState } from "react";

const ImageManagement = () => {
  const [imageData, setImageData] = useState([
    { id: 1, url: "https://via.placeholder.com/150" },
    { id: 2, url: "https://via.placeholder.com/150" },
    { id: 3, url: "https://via.placeholder.com/150" },
    { id: 4, url: "https://via.placeholder.com/150" },
    { id: 5, url: "https://via.placeholder.com/150" },
  ]);

  const [newImage, setNewImage] = useState(null);
  const [newImageId, setNewImageId] = useState(""); // Dropdown for IDs 1-6
  const [successMessage, setSuccessMessage] = useState("");
  const [deleteConfirmation, setDeleteConfirmation] = useState("");

  // Handle image file selection
  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      setNewImage(URL.createObjectURL(file));
    }
  };

  // Add new image to the table
  const handleAddImage = () => {
    if (!newImage || !newImageId) {
      setSuccessMessage("Please select an image and an ID.");
      setTimeout(() => setSuccessMessage(""), 3000);
      return;
    }

    // Check if the ID is already used
    if (imageData.some((img) => img.id === parseInt(newImageId))) {
      setSuccessMessage("This ID is already in use. Choose a different ID.");
      setTimeout(() => setSuccessMessage(""), 3000);
      return;
    }

    setImageData([...imageData, { id: parseInt(newImageId), url: newImage }]);
    setNewImage(null);
    setNewImageId("");
    setSuccessMessage("Image added successfully!");
    setTimeout(() => setSuccessMessage(""), 3000);
  };

  // Delete an image
  const handleDeleteImage = (id) => {
    setImageData(imageData.filter((image) => image.id !== id));
    setDeleteConfirmation("Image deleted successfully.");
    setTimeout(() => setDeleteConfirmation(""), 3000);
  };

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold mb-6">Image Management</h2>

      {/* Success Message */}
      {successMessage && (
        <div className="fixed top-4 right-4 bg-green-500 text-white p-4 rounded-md shadow-lg z-50">
          {successMessage}
        </div>
      )}

      {/* Delete Confirmation */}
      {deleteConfirmation && (
        <div className="fixed top-4 left-4 bg-red-500 text-white p-4 rounded-md shadow-lg z-50">
          {deleteConfirmation}
        </div>
      )}

      {/* Image Upload Section */}
      <div className="mb-4 flex items-center">
        <select
          value={newImageId}
          onChange={(e) => setNewImageId(e.target.value)}
          className="border border-gray-400 p-2 rounded-md mr-4"
        >
          <option value="">Select ID</option>
          {[1, 2, 3, 4, 5, 6].map((id) => (
            <option key={id} value={id}>
              {id}
            </option>
          ))}
        </select>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          className="border border-gray-400 p-2 rounded-md mr-4"
        />
        <button
          onClick={handleAddImage}
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Add Image
        </button>
      </div>

      {/* Image Table */}
      <div>
        <table className="w-full table-auto bg-white text-left">
          <thead className="bg-orange-400 text-white">
            <tr>
              <th className="p-4">ID</th>
              <th className="p-4">Preview</th>
              <th className="p-4">Actions</th>
            </tr>
          </thead>
          <tbody>
            {imageData.map((image) => (
              <tr key={image.id} className="border-b border-black">
                <td className="p-4">{image.id}</td>
                <td className="p-4">
                  <img
                    src={image.url}
                    alt={`Image ${image.id}`}
                    className="w-16 h-16 object-cover"
                  />
                </td>
                <td className="p-4">
                  <button
                    onClick={() => handleDeleteImage(image.id)}
                    className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ImageManagement;
