import React, { useState } from "react";
import { Link } from "react-router-dom";
import Sidebar from "./Sidebar"; // Assuming Sidebar is in the same directory

const Home_Page = () => {
  const [searchTerm, setSearchTerm] = useState(""); // State for search input
  const [searchField, setSearchField] = useState("name"); // State for search filter field
  const [currentPage, setCurrentPage] = useState(1); // State for pagination
  const itemsPerPage = 8; // Number of rows per page
  const [data, setData] = useState([
    // Initial data here...
  ]);

  const [countryDropdownOpen, setCountryDropdownOpen] = useState(false); // State to toggle country dropdown
  const [statusDropdownOpen, setStatusDropdownOpen] = useState(false); // State to toggle status dropdown
  const [statusFilter, setStatusFilter] = useState(""); // State for status filter

  const [selectedMessage, setSelectedMessage] = useState(null); // State to hold selected message for modal
  const [selectedSender, setSelectedSender] = useState(""); // State to hold selected sender name for modal

  const handleCountryFilter = (country) => {
    setSearchTerm(country);
    setSearchField("country");
    setCountryDropdownOpen(false); // Close country dropdown after selection
  };

  const handleStatusFilter = (status) => {
    setStatusFilter(status);
    setStatusDropdownOpen(false); // Close status dropdown after selection
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentData = data
    .filter((item) =>
      (searchField === "name" && item.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (searchField === "email" && item.email.toLowerCase().includes(searchTerm.toLowerCase())) ||
      (searchField === "country" && item.country.toLowerCase().includes(searchTerm.toLowerCase()))
    )
    .filter((item) => (statusFilter ? item.status === statusFilter : true)) // Filter by status
    .slice(indexOfFirstItem, indexOfLastItem);

  const totalPages = Math.ceil(
    data
      .filter((item) =>
        (searchField === "name" && item.name.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (searchField === "email" && item.email.toLowerCase().includes(searchTerm.toLowerCase())) ||
        (searchField === "country" && item.country.toLowerCase().includes(searchTerm.toLowerCase()))
      )
      .filter((item) => (statusFilter ? item.status === statusFilter : true)).length / itemsPerPage
  );

  const handleViewMessage = (name, message) => {
    setSelectedSender(name); // Set the sender's name for the modal
    setSelectedMessage(message); // Set the message to be displayed in the modal
  };

  const closeMessageModal = () => {
    setSelectedSender(""); // Clear the sender's name
    setSelectedMessage(null); // Close the modal by resetting the selected message
  };

  return (
    <div className="flex">
      {/* Sidebar */}
      <Sidebar />

      {/* Main Content Area */}
      <div className="flex-1 min-h-screen bg-gray-300 p-6 ml-64"> {/* Added margin for sidebar */}
        <div className="mb-6 flex justify-between items-center">
          <h1 className="text-2xl font-bold text-black">Manage booking</h1>
          <input
            type="text"
            placeholder="Search"
            className="w-64 p-2 rounded-md border border-gray-400 focus:outline-none focus:ring-2 focus:ring-orange-400"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>

        {/* Data Table */}
        <div className="overflow-x-auto bg-white shadow-md rounded-lg">
          <table className="w-full table-auto text-left">
            <thead className="bg-orange-400 text-white">
              <tr>
                <th className="p-4">ID</th>
                <th className="p-4">Name</th>
                <th className="p-4">Email</th>
                <th className="p-4">Phone</th>
                <th className="p-4">
                  Country
                  <button onClick={() => setCountryDropdownOpen(!countryDropdownOpen)}>
                    &#9660;
                  </button>
                  {countryDropdownOpen && (
                    <div className="absolute bg-gray-50 text-black shadow-lg rounded-lg p-2 mt-1 border border-gray-200">
                      <div onClick={() => handleCountryFilter("USA")} className="p-2 hover:bg-orange-400 hover:text-white rounded-md cursor-pointer">USA</div>
                      <div onClick={() => handleCountryFilter("Canada")} className="p-2 hover:bg-orange-400 hover:text-white rounded-md cursor-pointer">Canada</div>
                      <div onClick={() => handleCountryFilter("Norway")} className="p-2 hover:bg-orange-400 hover:text-white rounded-md cursor-pointer">Norway</div>
                    </div>
                  )}
                </th>
                <th className="p-4">Message</th>
                <th className="p-4">
                  Booking Status
                  <button onClick={() => setStatusDropdownOpen(!statusDropdownOpen)}>
                    &#9660;
                  </button>
                  {statusDropdownOpen && (
                    <div className="absolute bg-gray-50 text-black shadow-lg rounded-lg p-2 mt-1 border border-gray-200">
                      <div onClick={() => handleStatusFilter("Waiting")} className="p-2 hover:bg-orange-400 hover:text-white rounded-md cursor-pointer">Waiting</div>
                      <div onClick={() => handleStatusFilter("Finished")} className="p-2 hover:bg-orange-400 hover:text-white rounded-md cursor-pointer">Finished</div>
                      <div onClick={() => handleStatusFilter("")} className="p-2 hover:bg-orange-400 hover:text-white rounded-md cursor-pointer">All</div>
                    </div>
                  )}
                </th>
              </tr>
            </thead>
            <tbody>
              {currentData.map((item) => (
                <tr key={item.id} className="border-b last:border-none">
                  <td className="p-4 text-black">{item.id}</td>
                  <td className="p-4 text-black">{item.name}</td>
                  <td className="p-4 text-blue-500 underline">
                    <a href={`mailto:${item.email}`}>{item.email}</a>
                  </td>
                  <td className="p-4 text-blue-500 underline">
                    <a href={`tel:${item.phone}`}>{item.phone}</a>
                  </td>
                  <td className="p-4 text-black">{item.country}</td>
                  <td className="p-4">
                    <button onClick={() => handleViewMessage(item.name, item.message)} className="text-blue-500 underline hover:text-orange-500">View</button>
                  </td>
                  <td className="p-4">
                    <select
                      value={item.status}
                      onChange={(e) => {
                        setData((prevData) =>
                          prevData.map((dataItem) =>
                            dataItem.id === item.id
                              ? { ...dataItem, status: e.target.value }
                              : dataItem
                          )
                        );
                      }}
                      className={`p-2 rounded-md border border-gray-400 ${item.status === "Finished" ? "bg-green-500" : "bg-gray-300"} focus:outline-none focus:ring-2 focus:ring-orange-400`}
                    >
                      <option value="Waiting">Waiting</option>
                      <option value="Finished">Finished</option>
                    </select>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        {/* Pagination */}
        <div className="mt-4 flex justify-end items-center gap-4 flex justify-center">
          <button
            className={`px-4 py-2 rounded-md ${
              currentPage === 1
                ? "bg-gray-400 text-white cursor-not-allowed"
                : "bg-orange-400 text-white hover:bg-orange-500"
            }`}
            disabled={currentPage === 1}
            onClick={() => setCurrentPage((prev) => prev - 1)}
          >
            Previous
          </button>
          <span className="text-black">
            Page {currentPage} of {totalPages}
          </span>
          <button
            className={`px-4 py-2 rounded-md ${
              currentPage === totalPages
                ? "bg-gray-400 text-white cursor-not-allowed"
                : "bg-orange-400 text-white hover:bg-orange-500"
            }`}
            disabled={currentPage === totalPages}
            onClick={() => setCurrentPage((prev) => prev + 1)}
          >
            Next
          </button>
        </div>

        {/* Modal to display message */}
        {selectedMessage && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg max-w-md w-full">
              <h2 className="text-xl font-bold mb-4">{selectedSender}</h2>
              <p className="text-gray-800">{selectedMessage}</p>
              <button
                onClick={closeMessageModal}
                className="mt-4 bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
              >
                Close
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home_Page;
