import React, { useState } from "react";
import { Link } from "react-router-dom";

const PlaceManagement = () => {
  const [places, setPlaces] = useState([]);
  const [search, setSearch] = useState("");
  const [formData, setFormData] = useState({
    date: "",
    description: "",
    image: "",
    place: "",
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [modalMessage, setModalMessage] = useState("");
  const [modalType, setModalType] = useState("");
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [videoToDelete, setVideoToDelete] = useState(null); // The item to be deleted
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false); // New state for delete modal

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prevData) => ({ ...prevData, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAdd = () => {
    const { date, description, image, place } = formData;
    if (!date || !description || !place || !image) {
      setModalMessage("Date, Description, Place, and Image are required.");
      setModalType("error");
      setShowModal(true);
      return;
    }
    setPlaces([...places, formData]);
    setFormData({ date: "", description: "", image: "", place: "" });
    setShowModal(true);
    setModalMessage("Place added successfully!");
    setModalType("success");
  };

  const handleEdit = (index) => {
    const isConfirmed = window.confirm("Are you sure you want to edit this place?");
    if (isConfirmed) {
      setFormData(places[index]);
      setEditingIndex(index);
    }
  };

  const handleUpdate = () => {
    if (editingIndex !== null) {
      const updatedPlaces = [...places];
      updatedPlaces[editingIndex] = formData;
      setPlaces(updatedPlaces);
      setEditingIndex(null);
      setFormData({ date: "", description: "", image: "", place: "" });
      setShowModal(true);
      setModalMessage("Place updated successfully!");
      setModalType("success");
    }
  };

  // Handle the delete button click
  const handleDelete = (index) => {
    setVideoToDelete(places[index]); // Set the video/place to be deleted
    setIsDeleteModalOpen(true); // Open delete confirmation modal
  };

  // Handle the confirmation to delete the video/place
  const handleConfirmDelete = () => {
    if (videoToDelete) {
      const updatedPlaces = places.filter(
        (place) => place !== videoToDelete
      );
      setPlaces(updatedPlaces); // Remove the item from the places list
      setIsDeleteModalOpen(false); // Close the delete modal
      setModalMessage("Place deleted successfully!");
      setModalType("success");
      setShowModal(true); // Show success modal
    }
  };

  // Handle canceling the delete action
  const handleCancelDelete = () => {
    setIsDeleteModalOpen(false); // Close the delete confirmation modal
  };

  const filteredPlaces = places.filter(
    ({ date, description, place }) =>
      date.toLowerCase().includes(search.toLowerCase()) ||
      description.toLowerCase().includes(search.toLowerCase()) ||
      place.toLowerCase().includes(search.toLowerCase())
  );

  const totalPages = Math.ceil(filteredPlaces.length / 5);
  const currentPage = 1;

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4 text-black">Place management</h1>

      {/* Delete Confirmation Modal */}
      {isDeleteModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <h3 className="text-xl font-bold mb-4">Are you sure you want to delete this place?</h3>
            <div className="flex gap-2 justify-center">
              <button
                onClick={handleConfirmDelete}
                className="bg-red-500 text-white px-4 py-2 rounded-md"
              >
                Yes
              </button>
              <button
                onClick={handleCancelDelete}
                className=" text-gray-400 px-4 py-2 rounded-md"
              >
                No
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Search Bar */}
      <div className="flex justify-end mb-6">
        <input
          placeholder="Search..."
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="border border-gray-400 rounded p-2 w-64"
        />
      </div>

      {/* Add Button */}
      <div className="mb-6 flex justify-end">
        <button
          onClick={() => setShowModal((prevState) => !prevState)} // Toggle card visibility
          className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-green-500"
        >
          {showModal ? "Cancel" : "Add"}
        </button>
      </div>

      {/* Add Place Form Card */}
      {showModal && (
        <div className="mb-6 bg-gray-200 p-6 rounded shadow-md w-full max-w-lg mx-auto">
          <h2 className="text-xl font-semibold mb-4">{editingIndex !== null ? "Edit Place" : "Add Place"}</h2>

          <input
            placeholder="Date"
            type="date"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            placeholder="Description"
            type="text"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            placeholder="Place"
            type="text"
            name="place"
            value={formData.place}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            type="file"
            onChange={handleImageChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />

          <div className="flex justify-end">
            <button
              onClick={editingIndex !== null ? handleUpdate : handleAdd}
              className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
            >
              {editingIndex !== null ? "Update" : "Add"}
            </button>
          </div>
        </div>
      )}

      {/* Data Table */}
      <table className="table-auto w-full border-collapse border border-gray-400">
        <thead>
          <tr>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Date</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Description</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Place</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Image</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredPlaces.map((place, index) => (
            <tr key={index} className="border-b hover:bg-gray-50">
              <td className="px-4 py-2 text-black">{place.date}</td>
              <td className="px-4 py-2 text-black">{place.description}</td>
              <td className="px-4 py-2 text-black">{place.place}</td>
              <td className="px-4 py-2">
                {place.image && (
                  <img
                    src={place.image}
                    alt={place.description}
                    className="w-16 h-16 object-cover rounded"
                  />
                )}
              </td>
              <td className="px-4 py-2">
                <button
                  onClick={() => handleEdit(index)}
                  className="bg-blue-400 text-white py-1 px-3 rounded hover:bg-blue-500 mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(index)}
                  className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="mt-4 flex justify-center items-center gap-4">
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
        >
          Previous
        </button>
        <span className="text-black">
          Page {currentPage} of {totalPages || 1}
        </span>
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages || totalPages === 0
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages || totalPages === 0}
          onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default PlaceManagement;
