import React, { useState } from "react";
import { Link } from "react-router-dom";

const ImageManagement = () => {
  const [imageData, setImageData] = useState([
    {
      id: 1,
      title: "Sample Image 1",
      url: "https://via.placeholder.com/150",
    },
    {
      id: 2,
      title: "Sample Image 2",
      url: "https://via.placeholder.com/150",
    },
    {
      id: 3,
      title: "Sample Image 3",
      url: "https://via.placeholder.com/150",
    },
    {
      id: 4,
      title: "Sample Image 4",
      url: "https://via.placeholder.com/150",
    },
    {
      id: 5,
      title: "Sample Image 5",
      url: "https://via.placeholder.com/150",
    },
    {
      id: 6,
      title: "Sample Image 6",
      url: "https://via.placeholder.com/150",
    },
  ]);

  const [newImages, setNewImages] = useState([]);
  const [selectedImages, setSelectedImages] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [deleteConfirmation, setDeleteConfirmation] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const [editingImageId, setEditingImageId] = useState(null);
  const [editedTitle, setEditedTitle] = useState("");
  const [editedImage, setEditedImage] = useState(null);

  const [selectedId, setSelectedId] = useState(0);

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const imageFiles = files.map((file) => ({
      id: Date.now() + Math.random(),
      title: file.name,
      url: URL.createObjectURL(file),
    }));

    setNewImages((prevImages) => [...prevImages, ...imageFiles]);
  };

  const handleSelectImage = (image) => {
    if (selectedImages.some((img) => img.id === image.id)) {
      setSelectedImages(selectedImages.filter((img) => img.id !== image.id));
    } else {
      setSelectedImages([...selectedImages, image]);
    }
  };

  const handleAddImages = () => {
    if (selectedImages.length > 0) {
      setImageData([...imageData, ...selectedImages]);
      setSelectedImages([]);
      setNewImages([]);
      setSuccessMessage("Images added successfully!");
      setTimeout(() => setSuccessMessage(""), 3000);
    } else {
      setErrorMessage("Please select at least one image to upload.");
      setTimeout(() => setErrorMessage(""), 3000);
    }
  };

  const handleDeleteImage = (id) => {
    setImageData(imageData.filter((image) => image.id !== id));
    setDeleteConfirmation("Image deleted successfully.");
    setTimeout(() => setDeleteConfirmation(""), 3000);
  };

  const handleEditImage = (id, newTitle) => {
    setImageData(
      imageData.map((image) =>
        image.id === id ? { ...image, title: newTitle } : image
      )
    );
  };

  const handleFilterById = (id) => {
    setSelectedId(id);
  };

  const filteredImages = imageData.filter(
    (img) =>
      (selectedId === 0 || img.id === selectedId) &&
      (img.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        img.id.toString().includes(searchQuery))
  );

  const paginatedImages = filteredImages.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const totalPages = Math.ceil(filteredImages.length / itemsPerPage);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-left mb-6">Image Management</h2>

      {successMessage && (
        <div className="bg-green-500 text-white p-4 rounded-md mb-4">
          {successMessage}
        </div>
      )}

      {deleteConfirmation && (
        <div className="bg-red-500 text-white p-4 rounded-md mb-4">
          {deleteConfirmation}
        </div>
      )}

      {errorMessage && (
        <div className="bg-yellow-500 text-white p-4 rounded-md mb-4">
          {errorMessage}
        </div>
      )}

      <div className="flex items-center mb-4">
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageUpload}
          className="border border-gray-400 p-2 rounded-md mr-4"
        />
        <button
          onClick={handleAddImages}
          className="bg-orange-400 text-white px-4 py-2 rounded-md"
        >
          Add Image
        </button>
      </div>

      <div className="flex items-center mb-4">
        <select
          onChange={(e) => handleFilterById(parseInt(e.target.value, 10))}
          value={selectedId}
          className="border border-gray-400 p-2 rounded-md mr-4"
        >
          <option value={0}>All IDs</option>
          {imageData.map((image) => (
            <option key={image.id} value={image.id}>
              ID {image.id}
            </option>
          ))}
        </select>

        <input
          type="text"
          placeholder="Search by title or ID"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="border border-gray-400 p-2 rounded-md"
        />
      </div>

      <table className="w-full table-auto bg-white text-left">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-4">ID</th>
            <th className="p-4">Title</th>
            <th className="p-4">Preview</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {paginatedImages.map((image) => (
            <tr key={image.id} className="border-b border-gray-400">
              <td className="p-4">{image.id}</td>
              <td className="p-4">{image.title}</td>
              <td className="p-4">
                <img
                  src={image.url}
                  alt={image.title}
                  className="w-16 h-16 object-cover"
                />
              </td>
              <td className="p-4">
                <button
                  onClick={() => handleEditImage(image.id, "New Title")}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteImage(image.id)}
                  className="bg-red-500 text-white px-4 py-2 rounded-md"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="flex justify-center items-center mt-4 gap-4">
        <button
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
          className={`px-4 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-400 text-white"
              : "bg-orange-400 text-white"
          }`}
        >
          Previous
        </button>
        <span className="text-gray-700">
          Page {currentPage} of {totalPages}
        </span>
        <button
          onClick={() =>
            setCurrentPage((prev) => Math.min(prev + 1, totalPages))
          }
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages
              ? "bg-gray-400 text-white"
              : "bg-orange-400 text-white"
          }`}
        >
          Next
        </button>
      </div>

      <div className="mt-6">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md"
        >
          Dashboard
        </Link>
      </div>
    </div>
  );
};

export default ImageManagement;
