import React, { useState } from "react";
import { Link } from "react-router-dom";

// Event Management Component
const EventManagement = () => {
  const [eventData, setEventData] = useState([
    { id: 1, name: "Event 1", description: "Event description for Event 1" },
    { id: 2, name: "Event 2", description: "Event description for Event 2" },
    { id: 3, name: "Event 3", description: "Event description for Event 3" },
  ]);

  const [newEvent, setNewEvent] = useState({ name: "", description: "" });
  const [editEvent, setEditEvent] = useState(null);
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [videoToDelete, setVideoToDelete] = useState(null);
  const [selectedEvent, setSelectedEvent] = useState(null); // Store selected event
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false); // State for delete confirmation modal
  const [successMessage, setSuccessMessage] = useState("");
  const [deleteConfirmation, setDeleteConfirmation] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  const eventsPerPage = 5;

  const handleCreateEvent = () => {
    if (newEvent.name && newEvent.description) {
      setEventData([...eventData, { id: eventData.length + 1, ...newEvent }]);
      setNewEvent({ name: "", description: "" });
      setSuccessMessage("Event added successfully.");
      setTimeout(() => setSuccessMessage(""), 3000);
    } else {
      setErrorMessage("Please fill all fields for the event.");
      setTimeout(() => setErrorMessage(""), 3000);
    }
  };

  const handleEditEvent = (event) => {
    setEditEvent(event);
    setNewEvent({ name: event.name, description: event.description });
  };

  const handleSaveEdit = () => {
    if (newEvent.name && newEvent.description && editEvent) {
      setEventData((prevData) =>
        prevData.map((event) =>
          event.id === editEvent.id ? { ...event, ...newEvent } : event
        )
      );
      setNewEvent({ name: "", description: "" });
      setSuccessMessage("Event updated successfully.");
      setTimeout(() => setSuccessMessage(""), 3000);
      setEditEvent(null);
    } else {
      setErrorMessage("Please fill all fields for the event.");
      setTimeout(() => setErrorMessage(""), 3000);
    }
  };

  const handleCancelEdit = () => {
    setEditEvent(null);
    setNewEvent({ name: "", description: "" });
  };

  const handleDeleteEvent = (event) => {
    setVideoToDelete(event);
    setIsDeleteModalOpen(true); // Open the delete confirmation modal
  };

  const confirmDeleteEvent = () => {
    setEventData(eventData.filter((event) => event.id !== videoToDelete.id));
    setIsDeleteModalOpen(false); // Close the modal
    setDeleteConfirmation("Event deleted successfully.");
    setTimeout(() => setDeleteConfirmation(""), 3000);
  };

  const cancelDelete = () => {
    setIsDeleteModalOpen(false); // Close the modal
    setVideoToDelete(null);
  };

  const filteredEvents = eventData.filter((event) =>
    event.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totalPages = Math.ceil(filteredEvents.length / eventsPerPage);
  const currentEvents = filteredEvents.slice(
    (currentPage - 1) * eventsPerPage,
    currentPage * eventsPerPage
  );

  const handleViewDescription = (event) => {
    setSelectedEvent(event);
    setShowDeleteModal(true);
  };

  const closeDescriptionModal = () => {
    setShowDeleteModal(false);
    setSelectedEvent(null); // Clear the selected event
  };

  return (
    <div className="min-h-screen bg-gray-300 p-6 flex flex-col">
      <h2 className="text-2xl font-bold text-left mb-4">Event management</h2>

      {/* Confirmation Messages - Card Style */}
      {successMessage && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-green-500 p-6 rounded-lg shadow-lg text-white max-w-sm w-full">
            <h3 className="text-lg font-bold mb-4">{successMessage}</h3>
          </div>
        </div>
      )}

      {deleteConfirmation && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-green-500 p-6 rounded-lg shadow-lg text-white max-w-sm w-full">
            <h3 className="text-lg font-bold mb-4">{deleteConfirmation}</h3>
          </div>
        </div>
      )}

      {errorMessage && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-yellow-500 p-6 rounded-lg shadow-lg text-white max-w-sm w-full">
            <h3 className="text-lg font-bold mb-4">{errorMessage}</h3>
          </div>
        </div>
      )}

      {/* Search functionality */}
      <div className="mb-3 flex justify-end">
        <input
          type="text"
          placeholder="Search Events"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="p-2 text-sm rounded-md border border-gray-400 w-full sm:w-60"
        />
      </div>

      {/* New Event Form */}
      <div className="mb-3 grid grid-cols-1 sm:grid-cols-3 gap-4">
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
          className="p-2 text-sm rounded-md border border-gray-400 w-full"
        />
        <input
          type="text"
          placeholder="Description"
          value={newEvent.description}
          onChange={(e) =>
            setNewEvent({ ...newEvent, description: e.target.value })
          }
          className="p-2 text-sm rounded-md border border-gray-400 w-full"
        />
        <div className="flex gap-2 justify-end">
          <button
            onClick={editEvent ? handleSaveEdit : handleCreateEvent}
            className="bg-orange-400 text-white rounded-md text-base text-center px-3 py-2"
          >
            {editEvent ? "Save" : "Add"}
          </button>
          {editEvent && (
            <button
              onClick={handleCancelEdit}
              className="bg-gray-500 text-white rounded-md text-base text-center px-3 py-2 w-60"
            >
              Cancel
            </button>
          )}
        </div>
      </div>

      {/* Events Table */}
      <table className="w-full table-auto text-left mt-4 bg-white rounded-md shadow-md text-base">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-2">ID</th>
            <th className="p-2">Name</th>
            <th className="p-2">Description</th>
            <th className="p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentEvents.map((event, index) => (
            <tr key={event.id} className="border-b">
              <td className="p-2">{event.id}</td>
              <td className="p-2">{event.name}</td>
              <td className="p-2">
                <Link
                  to="#"
                  onClick={() => handleViewDescription(event)}
                  className="text-blue-500 hover:underline"
                >
                  View
                </Link>
              </td>
              <td className="p-2 flex gap-2">
                <button
                  onClick={() => handleEditEvent(event)}
                  className="bg-blue-500 text-white rounded-md text-base px-3 py-1"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteEvent(event)}
                  className="bg-red-500 text-white rounded-md text-base px-3 py-1"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination Controls */}
      <div className="mt-3 flex justify-center items-center gap-3 text-base">
        <button
          className={`px-3 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => prev - 1)}
        >
          Previous
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button
          className={`px-3 py-2 rounded-md ${
            currentPage === totalPages
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage((prev) => prev + 1)}
        >
          Next
        </button>
      </div>

      {/* Delete Confirmation Modal */}
      {isDeleteModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <h3 className="text-xl font-bold mb-4">
              Are you sure you want to delete this event?
            </h3>
            <div className="flex gap-2 justify-center">
              <button
                onClick={confirmDeleteEvent}
                className="bg-red-500 text-white px-4 py-2 rounded-md"
              >
                Yes
              </button>
              <button
                onClick={cancelDelete}
                className="text-gray-400 px-4 py-2 rounded-md"
              >
                No
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Event Description Modal */}
      {showDeleteModal && selectedEvent && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-md shadow-md">
            <h3 className="text-xl">Event Description: {selectedEvent.name}</h3>
            <div className="mt-4 p-4 bg-gray-100 rounded-md shadow-md max-w-xs">
              {selectedEvent.description}
            </div>
            <div className="mt-4">
              <button
                onClick={closeDescriptionModal}
                className="bg-gray-500 text-white rounded-md px-4 py-2"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Link to Dashboard */}
      <div className="mt-auto flex justify-left">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-3 py-2 rounded-md text-base"
        >
          Dashboard
        </Link>
      </div>
    </div>
  );
};

export default EventManagement;
