import React, { useState } from "react";
import { Link } from "react-router-dom";

const ImageManagement = () => {
  const [imageData, setImageData] = useState([]);
  const [newImages, setNewImages] = useState([]);
  const [albumName, setAlbumName] = useState("");
  const [selectedImages, setSelectedImages] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [currentAlbumId, setCurrentAlbumId] = useState(1);
  const [successMessage, setSuccessMessage] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const [editingImageId, setEditingImageId] = useState(null); 
  const [editedTitle, setEditedTitle] = useState(""); 
  const [editedImage, setEditedImage] = useState(null);

  // Track which album is being edited
  const [editingAlbumId, setEditingAlbumId] = useState(null);
  const [editedAlbumName, setEditedAlbumName] = useState("");

  // Handle image file upload
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const imageFiles = files.map((file) => ({
      id: Date.now() + Math.random(),
      albumId: null,
      albumName: "",
      title: file.name,
      url: URL.createObjectURL(file),
    }));

    setNewImages((prevImages) => [...prevImages, ...imageFiles]);
  };

  // Add selected images to imageData as a new album or update an existing album
  const handleAddImages = () => {
    if (!albumName.trim()) {
      alert("Please provide an album name.");
      return;
    }

    // Check if album name already exists
    const existingAlbum = imageData.find((img) => img.albumName.toLowerCase() === albumName.toLowerCase());

    if (existingAlbum) {
      // Update existing album
      if (selectedImages.length > 0) {
        const updatedImages = selectedImages.map((image) => ({
          ...image,
          albumId: existingAlbum.albumId,
          albumName: albumName,
          id: Date.now() + Math.random(), // Create a new unique ID for the new images
        }));

        // Update imageData with the new images in the existing album
        setImageData(imageData.map((img) =>
          img.albumId === existingAlbum.albumId ? { ...img, images: [...img.images, ...updatedImages] } : img
        ));

        setSelectedImages([]);
        setNewImages([]);
        setAlbumName("");
        setSuccessMessage(`Album "${albumName}" (#${existingAlbum.albumId}) updated successfully!`);
        setTimeout(() => setSuccessMessage(""), 3000);
      } else {
        alert("Please select at least one image to add to the album.");
      }
    } else {
      // Create new album
      if (selectedImages.length > 0) {
        const albumId = currentAlbumId;
        const imagesWithAlbum = selectedImages.map((image) => ({
          ...image,
          albumId,
          albumName,
          id: Date.now() + Math.random(),
        }));

        setImageData([...imageData, ...imagesWithAlbum]);
        setSelectedImages([]);
        setNewImages([]);
        setAlbumName("");
        setSuccessMessage(`Album "${albumName}" (#${albumId}) created successfully!`);
        setTimeout(() => setSuccessMessage(""), 3000);
        setCurrentAlbumId(albumId + 1);
      } else {
        alert("Please select at least one image to create an album.");
      }
    }
  };

  // Select/Unselect images for upload
  const handleSelectImage = (image) => {
    if (selectedImages.some((img) => img.id === image.id)) {
      setSelectedImages(selectedImages.filter((img) => img.id !== image.id));
    } else {
      setSelectedImages([...selectedImages, image]);
    }
  };

  // Delete image
  const handleDeleteImage = (id) => {
    setImageData(imageData.filter((img) => img.id !== id));
  };

  // Start editing an image
  const handleEditImage = (image) => {
    setEditingImageId(image.id);
    setEditedTitle(image.title); 
    setEditedImage(image.url); 
  };

  // Save updated image title and image
  const handleSaveEdit = () => {
    if (editedImage) {
      setImageData(
        imageData.map((img) =>
          img.id === editingImageId
            ? { ...img, title: editedTitle, url: editedImage }
            : img
        )
      );
    } else {
      setImageData(
        imageData.map((img) =>
          img.id === editingImageId ? { ...img, title: editedTitle } : img
        )
      );
    }
    setEditingImageId(null);
    setEditedTitle("");
    setEditedImage(null);
  };

  // Cancel editing
  const handleCancelEdit = () => {
    setEditingImageId(null);
    setEditedTitle("");
    setEditedImage(null);
  };

  // Handle album name edit
  const handleEditAlbumName = (albumId, currentName) => {
    setEditingAlbumId(albumId);
    setEditedAlbumName(currentName);
  };

  // Save edited album name
  const handleSaveAlbumName = (albumId) => {
    setImageData(
      imageData.map((img) =>
        img.albumId === albumId ? { ...img, albumName: editedAlbumName } : img
      )
    );
    setEditingAlbumId(null);
    setEditedAlbumName("");
  };

  // Cancel album name editing
  const handleCancelAlbumName = () => {
    setEditingAlbumId(null);
    setEditedAlbumName("");
  };

  // Handle search query
  const filteredAlbums = imageData
    .filter(
      (img) =>
        img.albumName.toLowerCase().includes(searchQuery.toLowerCase()) ||
        img.albumId.toString().includes(searchQuery)
    )
    .reduce((acc, img) => {
      const albumExists = acc.some((album) => album.albumId === img.albumId);
      if (!albumExists) {
        acc.push({
          albumId: img.albumId,
          albumName: img.albumName,
          images: imageData.filter((i) => i.albumId === img.albumId),
        });
      }
      return acc;
    }, []);

  // Paginate filtered albums
  const paginatedAlbums = filteredAlbums.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const totalPages = Math.ceil(filteredAlbums.length / itemsPerPage);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Image Management with Albums</h2>

      {successMessage && (
        <div className="fixed top-4 right-4 bg-green-500 text-white p-4 rounded-md shadow-lg z-50">
          {successMessage}
        </div>
      )}

      {/* Album Name Input */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Enter Album Name"
          value={albumName}
          onChange={(e) => setAlbumName(e.target.value)}
          className="block mb-4 border border-gray-400 p-2 rounded-md w-1/1"
        />
      </div>

      {/* Image Upload Section */}
      <div className="mb-4">
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageUpload}
          className="block mb-4"
        />
      </div>

      {newImages.length > 0 && (
        <div className="grid grid-cols-4 gap-4 mb-6">
          {newImages.map((image) => (
            <div
              key={image.id}
              className={`p-2 border rounded-md cursor-pointer ${
                selectedImages.some((img) => img.id === image.id) ? "bg-orange-200" : "bg-white"
              }`}
              onClick={() => handleSelectImage(image)}
            >
              <img src={image.url} alt={image.title} className="w-full h-32 object-cover mb-2" />
              <div className="text-sm text-center font-semibold">{image.title}</div>
            </div>
          ))}
        </div>
      )}

      {newImages.length > 0 && (
        <button
          onClick={handleAddImages}
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Create or Update Album
        </button>
      )}

      <div className="absolute top-4 right-6">
        <input
          type="text"
          placeholder="Search by Album Name or ID"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="border border-orange-400 p-2 rounded-md w-2/2"
        />
      </div>

      <div>
        {paginatedAlbums.map((album) => (
          <div key={album.albumId} className="mb-6">
            <h3 className="text-lg font-bold mb-2">
              {editingAlbumId === album.albumId ? (
                <div className="flex items-center">
                  <input
                    type="text"
                    value={editedAlbumName}
                    onChange={(e) => setEditedAlbumName(e.target.value)}
                    className="border border-gray-400 p-2 rounded-md mr-2"
                  />
                  <button
                    onClick={() => handleSaveAlbumName(album.albumId)}
                    className="bg-green-500 text-white px-2 py-1 rounded-md"
                  >
                    Save
                  </button>
                  <button
                    onClick={handleCancelAlbumName}
                    className="bg-gray-500 text-white px-2 py-1 rounded-md ml-2"
                  >
                    Cancel
                  </button>
                </div>
              ) : (
                <div className="flex justify-between">
                  <span>
                    Album: "{album.albumName}" (#{album.albumId})
                  </span>
                  <button
                    onClick={() => handleEditAlbumName(album.albumId, album.albumName)}
                    className="bg-blue-500 text-white px-2 py-1 rounded-md hover:bg-blue-600"
                  >
                    Edit
                  </button>
                </div>
              )}
            </h3>

            <table className="w-full table-auto text-left">
              <thead className="bg-orange-400 text-white">
                <tr>
                  <th className="p-4">ID</th>
                  <th className="p-4">Title</th>
                  <th className="p-4">Preview</th>
                  <th className="p-4">Actions</th>
                </tr>
              </thead>
              <tbody>
                {album.images.map((image) => (
                  <tr key={image.id}>
                    <td className="p-4">{image.id}</td>
                    <td className="p-4">
                      {editingImageId === image.id ? (
                        <input
                          type="text"
                          value={editedTitle}
                          onChange={(e) => setEditedTitle(e.target.value)}
                          className="border border-gray-400 p-2 rounded-md"
                        />
                      ) : (
                        image.title
                      )}
                    </td>
                    <td className="p-4">
                      {editingImageId === image.id ? (
                        <input
                          type="file"
                          onChange={(e) => setEditedImage(URL.createObjectURL(e.target.files[0]))}
                          className="border p-2 rounded-md"
                        />
                      ) : (
                        <img src={image.url} alt={image.title} className="w-16 h-16 object-cover" />
                      )}
                    </td>
                    <td className="p-4">
                      {editingImageId === image.id ? (
                        <>
                          <button
                            onClick={handleSaveEdit}
                            className="bg-green-500 text-white px-2 py-1 rounded-md hover:bg-green-600 mr-2"
                          >
                            Save
                          </button>
                          <button
                            onClick={handleCancelEdit}
                            className="bg-gray-500 text-white px-2 py-1 rounded-md hover:bg-gray-600"
                          >
                            Cancel
                          </button>
                        </>
                      ) : (
                        <>
                          <button
                            onClick={() => handleEditImage(image)}
                            className="bg-blue-500 text-white px-2 py-1 rounded-md hover:bg-blue-600 mr-2"
                          >
                            Edit
                          </button>
                          <button
                            onClick={() => handleDeleteImage(image.id)}
                            className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600"
                          >
                            Delete
                          </button>
                        </>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>

      {/* Pagination Controls */}
      <div className="flex justify-center">
        <button
          onClick={() => setCurrentPage(Math.max(currentPage - 1, 1))}
          className="bg-gray-400 text-white px-4 py-2 rounded-md mr-4"
        >
          Previous
        </button>
        <span className="px-4 py-2">{currentPage} of {totalPages}</span>
        <button
          onClick={() => setCurrentPage(Math.min(currentPage + 1, totalPages))}
          className="bg-gray-400 text-white px-4 py-2 rounded-md ml-4"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default ImageManagement;
