import React, { useState } from "react";
import { Link } from "react-router-dom"; // Import Link for navigation

const PlaceManagement = () => {
  const [places, setPlaces] = useState([]);
  const [search, setSearch] = useState("");
  const [formData, setFormData] = useState({
    date: "",
    description: "",
    image: "",
    place: "",
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [confirmationMessage, setConfirmationMessage] = useState({
    show: false,
    text: "",
    type: "", // 'update' or 'delete'
  });

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Number of items per page

  const [messageBoxVisibility, setMessageBoxVisibility] = useState({
    visible: false,
    message: "",
  });

  // State for showing the "Add Place" card
  const [showAddCard, setShowAddCard] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prevData) => ({ ...prevData, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAdd = () => {
    const { date, description, image, place } = formData;
    if (!date || !description || !place || !image) {
      alert("Date, Description, Place, and Image are required.");
      return;
    }
    setPlaces([...places, formData]);
    setFormData({ date: "", description: "", image: "", place: "" });
    setShowAddCard(false); // Hide the card after adding
  };

  const handleEdit = (index) => {
    setFormData(places[index]);
    setEditingIndex(index);
  };

  const handleUpdate = () => {
    if (editingIndex !== null) {
      const updatedPlaces = [...places];
      updatedPlaces[editingIndex] = formData;
      setPlaces(updatedPlaces);
      setEditingIndex(null);
      setFormData({ date: "", description: "", image: "", place: "" });

      // Show update confirmation message
      setConfirmationMessage({
        show: true,
        text: "Place updated successfully!",
        type: "update",
      });
      setTimeout(() => {
        setConfirmationMessage({ show: false, text: "", type: "" });
      }, 3000); // Hide after 3 seconds
    }
  };

  const handleDelete = (index) => {
    // Confirmation dialog
    const isConfirmed = window.confirm("Are you sure you want to delete this place?");
    if (isConfirmed) {
      const updatedPlaces = places.filter((_, i) => i !== index);
      setPlaces(updatedPlaces);

      // Show delete confirmation message
      setConfirmationMessage({
        show: true,
        text: "Place deleted successfully!",
        type: "delete",
      });
      setTimeout(() => {
        setConfirmationMessage({ show: false, text: "", type: "" });
      }, 3000); // Hide after 3 seconds
    }
  };

  // Show message box
  const handleShowMessageBox = (message) => {
    setMessageBoxVisibility({ visible: true, message });
  };

  // Hide message box
  const handleCloseMessageBox = () => {
    setMessageBoxVisibility({ visible: false, message: "" });
  };

  // Filter places based on search
  const filteredPlaces = places.filter(
    ({ date, description, place }) =>
      date.toLowerCase().includes(search.toLowerCase()) ||
      description.toLowerCase().includes(search.toLowerCase()) ||
      place.toLowerCase().includes(search.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredPlaces.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredPlaces.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4 text-black">Place Management</h1>

      {/* Confirmation Message */}
      {confirmationMessage.show && (
        <div
          className={`mb-4 p-3 rounded ${
            confirmationMessage.type === "update"
              ? "bg-green-400"
              : confirmationMessage.type === "delete"
              ? "bg-red-500"
              : ""
          } text-white`}
        >
          {confirmationMessage.text}
        </div>
      )}

      {/* Search Bar moved to Top Right */}
      <div className="flex justify-end mb-6">
        <input
          placeholder="Search..."
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="border border-gray-400 rounded p-2 w-64"
        />
      </div>

      {/* Add Button */}
      <div className="mb-6 flex justify-end">
        <button
          onClick={() => setShowAddCard(!showAddCard)}
          className="bg-green-400 text-white py-2 px-4 rounded hover:bg-green-500"
        >
          {showAddCard ? "Cancel" : "Add Place"}
        </button>
      </div>

      {/* Add Place Form Card */}
      {showAddCard && (
        <div className="mb-6 bg-gray-300 p-6 rounded shadow-md w-full max-w-lg mx-auto">
          <h2 className="text-xl font-semibold mb-4">Add Place</h2>

          <input
            placeholder="Date"
            type="date"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            placeholder="Description"
            type="text"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            placeholder="Place"
            type="text"
            name="place"
            value={formData.place}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            type="file"
            onChange={handleImageChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />

          <div className="flex justify-end">
            <button
              onClick={handleAdd}
              className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
            >
              Add
            </button>
          </div>
        </div>
      )}

      {/* Data Table */}
      <table className="table-auto w-full border-collapse border border-gray-400">
        <thead>
          <tr>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Date</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Description</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Place</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Image</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentItems.map((place, index) => (
            <tr key={index} className="border-b hover:bg-gray-50">
              <td className="px-4 py-2 text-black">{place.date}</td>
              <td className="px-4 py-2 text-black">{place.description}</td>
              <td className="px-4 py-2 text-black">{place.place}</td>
              <td className="px-4 py-2">
                {place.image && (
                  <img
                    src={place.image}
                    alt={place.description}
                    className="w-16 h-16 object-cover rounded"
                  />
                )}
              </td>
              <td className="px-4 py-2">
                <button
                  onClick={() => handleEdit(indexOfFirstItem + index)}
                  className="bg-blue-400 text-white py-1 px-3 rounded hover:bg-blue-500 mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(indexOfFirstItem + index)}
                  className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="mt-4 flex justify-center items-center gap-4">
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
        >
          Previous
        </button>
        <span className="text-black">
          Page {currentPage} of {totalPages || 1}
        </span>
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages || totalPages === 0
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages || totalPages === 0}
          onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
        >
          Next
        </button>
      </div>

      {/* Dashboard Button */}
      <div className="mt-6 flex justify-left">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Dashboard
        </Link>
      </div>

      {/* Message Box */}
      {messageBoxVisibility.visible && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
          <div className="bg-white p-6 rounded-md w-96 shadow-lg">
            <h2 className="text-xl font-semibold mb-4">External Message</h2>
            <p>{messageBoxVisibility.message}</p>
            <button
              onClick={handleCloseMessageBox}
              className="bg-red-500 text-white py-2 px-4 rounded mt-4 hover:bg-red-600"
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default PlaceManagement;
