import React, { useState } from "react";
import { Link } from "react-router-dom";

const Home_Page = () => {
  const [searchTerm, setSearchTerm] = useState(""); // State for search input
  const [searchField, setSearchField] = useState("name"); // State for search filter field
  const [currentPage, setCurrentPage] = useState(1); // State for pagination
  const itemsPerPage = 8; // Number of rows per page
  const [data, setData] = useState([
    {
      id: 1,
      name: "John Doe",
      email: "john@example.com",
      phone: "123-456-7890",
      country: "USA",
      message: "Inquiry about the product.",
      status: "Waiting",
    },
    {
      id: 2,
      name: "Jane Smith",
      email: "jane@example.com",
      phone: "987-654-3210",
      country: "Canada",
      message: "Need more details about the service.",
      status: "Finished",
    },
    {
      id: 3,
      name: "Harper King",
      email: "harper.k@example.com",
      phone: "567-890-1234",
      country: "Norway",
      message: "Feedback on customer service.",
      status: "Waiting",
    },
    // More data entries...
  ]);

  const [countryDropdownOpen, setCountryDropdownOpen] = useState(false); // State to toggle country dropdown
  const [statusDropdownOpen, setStatusDropdownOpen] = useState(false); // State to toggle status dropdown
  const [statusFilter, setStatusFilter] = useState(""); // State for status filter

  // Function to handle country filter
  const handleCountryFilter = (country) => {
    setSearchTerm(country);
    setSearchField("country");
    setCountryDropdownOpen(false); // Close country dropdown after selection
  };

  // Function to handle status filter
  const handleStatusFilter = (status) => {
    setStatusFilter(status);
    setStatusDropdownOpen(false); // Close status dropdown after selection
  };

  // Pagination logic
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentData = data
    .filter((item) =>
      item[searchField].toString().toLowerCase().includes(searchTerm.toLowerCase())
    )
    .filter((item) => (statusFilter ? item.status === statusFilter : true)) // Filter by status
    .slice(indexOfFirstItem, indexOfLastItem);

  const totalPages = Math.ceil(
    data
      .filter((item) =>
        item[searchField].toString().toLowerCase().includes(searchTerm.toLowerCase())
      )
      .filter((item) => (statusFilter ? item.status === statusFilter : true)).length / itemsPerPage
  );

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      {/* Heading and Search Input */}
      <div className="mb-6 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-black">Manage Booking</h1>
        <input
          type="text"
          placeholder="Search"
          className="w-64 p-2 rounded-md border border-gray-400 focus:outline-none focus:ring-2 focus:ring-orange-400"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Data Table */}
      <div className="overflow-x-auto bg-white shadow-md rounded-lg">
        <table className="w-full table-auto text-left">
          <thead className="bg-orange-400 text-white">
            <tr>
              <th className="p-4">Booking ID</th>
              <th className="p-4">Customer Name</th>
              <th className="p-4">Customer Email</th>
              <th className="p-4">Customer Phone</th>
              <th className="p-4">Customer Country</th>
              <th className="p-4">Booking Status</th> {/* Updated column header */}
              <th className="p-4">Click to View</th> {/* Updated column header */}
              <th className="p-4">Update Status</th>
            </tr>
          </thead>
          <tbody>
            {currentData.map((item) => (
              <tr key={item.id} className="border-b last:border-none">
                <td className="p-4 text-black">{item.id}</td>
                <td className="p-4 text-black">{item.name}</td>
                <td className="p-4 text-blue-500 underline">
                  <a href={`mailto:${item.email}`}>{item.email}</a>
                </td>
                <td className="p-4 text-blue-500 underline">
                  <a href={`tel:${item.phone}`}>{item.phone}</a>
                </td>
                <td className="p-4 text-black">{item.country}</td>
                <td className="p-4">
                  <select
                    value={item.status}
                    onChange={(e) => {
                      setData((prevData) =>
                        prevData.map((dataItem) =>
                          dataItem.id === item.id
                            ? { ...dataItem, status: e.target.value }
                            : dataItem
                        )
                      );
                    }}
                    className="p-2 rounded-md border border-gray-400"
                  >
                    <option value="Waiting">Waiting</option>
                    <option value="Finished">Finished</option>
                  </select>
                </td>
                <td className="p-4">
                  <button
                    onClick={() => alert(`Message from ${item.name}:\n${item.message}`)}
                    className="text-blue-500 underline hover:text-orange-500"
                  >
                    Click to View
                  </button>
                </td>
                <td className="p-4">
                  <select
                    value={item.status}
                    onChange={(e) => {
                      setData((prevData) =>
                        prevData.map((dataItem) =>
                          dataItem.id === item.id
                            ? { ...dataItem, status: e.target.value }
                            : dataItem
                        )
                      );
                    }}
                    className={`p-2 rounded-md border border-gray-400`}
                  >
                    <option value="Waiting">Waiting</option>
                    <option value="Finished">Finished</option>
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Pagination */}
      <div className="mt-4 flex justify-end items-center gap-4">
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => prev - 1)}
        >
          Previous
        </button>
        <span className="text-black">
          Page {currentPage} of {totalPages}
        </span>
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage((prev) => prev + 1)}
        >
          Next
        </button>
      </div>

      {/* Dashboard Button */}
      <div className="mt-6 flex justify-left">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Dashboard
        </Link>
      </div>
    </div>
  );
};

export default Home_Page;
