import React, { useState } from "react";
import { Link } from "react-router-dom";

const PlaceManagement = () => {
  const [places, setPlaces] = useState([]);
  const [search, setSearch] = useState("");
  const [formData, setFormData] = useState({
    date: "",
    description: "",
    image: "",
    place: "",
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [showModal, setShowModal] = useState(false); 
  const [modalMessage, setModalMessage] = useState(""); 
  const [modalType, setModalType] = useState(""); 

  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [videoToDelete, setVideoToDelete] = useState(null); 

  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); 

  const [showAddCard, setShowAddCard] = useState(false);

  const [selectedPlace, setSelectedPlace] = useState(null); // New state for selected place details

  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false); // Delete confirmation modal state
  const [successMessage, setSuccessMessage] = useState(""); // Success message
  const [deleteConfirmation, setDeleteConfirmation] = useState(""); // Delete confirmation message
  const [errorMessage, setErrorMessage] = useState(""); // Error message

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prevData) => ({ ...prevData, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAdd = () => {
    const { date, description, image, place } = formData;
    if (!date || !description || !place || !image) {
      setErrorMessage("Date, Description, Place, and Image are required.");
      setShowModal(true);
      return;
    }
    setPlaces([...places, formData]);
    setFormData({ date: "", description: "", image: "", place: "" });
    setShowAddCard(false);
    setSuccessMessage("Place added successfully!");
    setShowModal(true);
  };

  const handleEdit = (index) => {
    const isConfirmed = window.confirm("Are you sure you want to edit this place?");
    if (isConfirmed) {
      setFormData(places[index]);
      setEditingIndex(index);
      setShowAddCard(true);
    }
  };

  const handleUpdate = () => {
    if (editingIndex !== null) {
      const updatedPlaces = [...places];
      updatedPlaces[editingIndex] = formData;
      setPlaces(updatedPlaces);
      setEditingIndex(null);
      setFormData({ date: "", description: "", image: "", place: "" });
      setShowAddCard(false);
      setSuccessMessage("Place updated successfully!");
      setShowModal(true);
    }
  };

  const handleDelete = (index) => {
    setVideoToDelete(places[index]);
    setIsDeleteModalOpen(true);
  };

  const confirmDeleteVideo = () => {
    const updatedPlaces = places.filter((_, i) => i !== places.indexOf(videoToDelete));
    setPlaces(updatedPlaces);
    setIsDeleteModalOpen(false);
    setDeleteConfirmation("Place deleted successfully!");
    setShowModal(true);
  };

  const cancelDelete = () => {
    setIsDeleteModalOpen(false);
  };

  const filteredPlaces = places.filter(
    ({ date, description, place }) =>
      date.toLowerCase().includes(search.toLowerCase()) ||
      description.toLowerCase().includes(search.toLowerCase()) ||
      place.toLowerCase().includes(search.toLowerCase())
  );

  const totalPages = Math.ceil(filteredPlaces.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredPlaces.slice(indexOfFirstItem, indexOfLastItem);

  // Function to open the selected place details
  const handlePlaceClick = (place) => {
    setSelectedPlace(place); // Set the selected place to show in the card
  };

  // Function to close the place details card
  const handleClosePlaceCard = () => {
    setSelectedPlace(null); // Reset the selected place data
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4 text-black">Place management</h1>

      {/* Success, Error, and Delete Confirmation Messages */}
      {(successMessage || deleteConfirmation || errorMessage) && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center transition-all duration-300">
          <div className={`p-6 rounded-lg shadow-lg text-white max-w-sm w-full ${successMessage ? 'bg-green-500' : errorMessage ? 'bg-yellow-500' : 'bg-green-500'}`}>
            <h3 className="text-lg font-bold mb-4">
              {successMessage || deleteConfirmation || errorMessage}
            </h3>
          </div>
        </div>
      )}

      {/* Modal for Delete Confirmation */}
      {isDeleteModalOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center transition-all duration-300">
          <div className="bg-white p-6 rounded-md shadow-lg">
            <h3 className="text-xl font-bold mb-4">Are you sure you want to delete this image?</h3>
            <div className="flex gap-2 justify-center">
              <button
                onClick={confirmDeleteVideo}
                className="bg-red-500 text-white px-4 py-2 rounded-md transition-all duration-300"
              >
                Yes
              </button>
              <button
                onClick={cancelDelete}
                className=" text-gray-400 px-4 py-2 rounded-md transition-all duration-300"
              >
                No
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center transition-all duration-300">
          <div className="bg-gray-200 p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold mb-4">Confirm Deletion</h3>
            <p>Are you sure you want to delete "{videoToDelete?.place}"?</p>
            <div className="flex justify-end gap-4 mt-4">
              <button
                onClick={confirmDeleteVideo}
                className="bg-red-600 text-white px-4 py-2 rounded-md transition-all duration-300"
              >
                Delete
              </button>
              <button
                onClick={cancelDelete}
                className=" text-gray-500 px-4 py-2 rounded-md transition-all duration-300"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Search Bar */}
      <div className="flex justify-end mb-6">
        <input
          placeholder="Search..."
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="border border-gray-400 rounded p-2 w-64 transition-all duration-300"
        />
      </div>

      {/* Add Button */}
      <div className="mb-6 flex justify-end">
        <button
          onClick={() => setShowAddCard((prevState) => !prevState)} // Toggle card visibility
          className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-green-500 transition-all duration-300"
        >
          {showAddCard ? "Cancel" : "Add"}
        </button>
      </div>

      {/* Add Place Form Card */}
      {showAddCard && (
        <div className="mb-6 bg-gray-200 p-6 rounded shadow-md w-full max-w-lg mx-auto transition-all duration-300">
          <h2 className="text-xl font-semibold mb-4">{editingIndex !== null ? "Edit Place" : "Add Place"}</h2>

          <input
            placeholder="Date"
            type="date"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4 transition-all duration-300"
          />
          <input
            placeholder="Description"
            type="text"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4 transition-all duration-300"
          />
          <input
            placeholder="Place"
            type="text"
            name="place"
            value={formData.place}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4 transition-all duration-300"
          />
          <input
            type="file"
            onChange={handleImageChange}
            className="border border-gray-400 rounded p-2 w-full mb-4 transition-all duration-300"
          />

          <div className="flex justify-end">
            <button
              onClick={editingIndex !== null ? handleUpdate : handleAdd}
              className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500 transition-all duration-300"
            >
              {editingIndex !== null ? "Update" : "Add"}
            </button>
          </div>
        </div>
      )}

      {/* Data Table */}
      <table className="table-auto w-full border-collapse border border-gray-400 transition-all duration-300">
        <thead>
          <tr>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Date</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Description</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Place</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Image</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentItems.map((place, index) => (
            <tr
              key={index}
              className="border-b hover:bg-gray-50 transition-all duration-300"
            >
              <td className="px-4 py-2 text-black">{place.date}</td>
              <td className="px-4 py-2 text-black">
                <button
                  onClick={() => handlePlaceClick(place)} // Show full place details
                  className="text-blue-500 transition-all duration-300"
                >
                  View
                </button>
              </td>
              <td className="px-4 py-2 text-black">{place.place}</td>
              <td className="px-4 py-2">
                {place.image && (
                  <img
                    src={place.image}
                    alt={place.description}
                    className="w-16 h-16 object-cover rounded transition-all duration-300"
                  />
                )}
              </td>
              <td className="px-4 py-2">
                <button
                  onClick={() => handleEdit(index)}
                  className="bg-blue-400 text-white py-1 px-3 rounded hover:bg-blue-500 transition-all duration-300 mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(index)}
                  className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600 transition-all duration-300"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="mt-4 flex justify-center items-center gap-4">
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500 transition-all duration-300"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
        >
          Previous
        </button>
        <span className="text-black">
          Page {currentPage} of {totalPages || 1}
        </span>
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages || totalPages === 0
              ? "bg-gray-300 cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500 transition-all duration-300"
          }`}
          disabled={currentPage === totalPages || totalPages === 0}
          onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
        >
          Next
        </button>
      </div>

      {/* Place Details Card */}
      {selectedPlace && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center transition-all duration-300">
          <div className="bg-white p-6 rounded-md shadow-lg w-1/3">
            <h3 className="text-2xl font-bold mb-4">{selectedPlace.place}</h3>
            <p><strong>Date:</strong> {selectedPlace.date}</p>
            <p><strong>Description:</strong> {selectedPlace.description}</p>
            <div className="mt-4">
              <img
                src={selectedPlace.image}
                alt={selectedPlace.place}
                className="w-full h-48 object-cover rounded transition-all duration-300"
              />
            </div>
            <div className="flex justify-end mt-4">
              <button
                onClick={handleClosePlaceCard}
                className="bg-gray-500 text-white py-2 px-4 rounded transition-all duration-300"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PlaceManagement;
