import React, { useState } from "react";

const ImageManagement = () => {
  const [imageData, setImageData] = useState([
    { id: 1, images: ["https://via.placeholder.com/150"] },
    { id: 2, images: ["https://via.placeholder.com/150"] },
    { id: 3, images: ["https://via.placeholder.com/150"] },
  ]);

  const [newImages, setNewImages] = useState([]);
  const [newAlbumId, setNewAlbumId] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [deleteConfirmation, setDeleteConfirmation] = useState("");

  // Handle multiple image file selection
  const handleImageUpload = (e) => {
    const files = e.target.files;
    if (files) {
      const fileArray = Array.from(files).map((file) => URL.createObjectURL(file));
      setNewImages(fileArray);
    }
  };

  // Add new images to the selected album
  const handleAddImages = () => {
    if (!newImages.length || !newAlbumId) {
      setSuccessMessage("Please select images and an album ID.");
      setTimeout(() => setSuccessMessage(""), 3000);
      return;
    }

    setImageData(
      imageData.map((album) =>
        album.id === parseInt(newAlbumId)
          ? { ...album, images: [...album.images, ...newImages] }
          : album
      )
    );
    setNewImages([]);
    setNewAlbumId("");
    setSuccessMessage("Images added successfully!");
    setTimeout(() => setSuccessMessage(""), 3000);
  };

  // Delete all images in a specific album
  const handleDeleteAlbum = (id) => {
    setImageData(imageData.filter((album) => album.id !== id));
    setDeleteConfirmation("Album deleted successfully.");
    setTimeout(() => setDeleteConfirmation(""), 3000);
  };

  // Edit an album (replace all images with new ones)
  const handleEditAlbum = (id) => {
    const updatedImages = prompt("Enter new image URLs, separated by commas:");
    if (updatedImages) {
      const imageUrls = updatedImages.split(",").map((url) => url.trim());
      setImageData(
        imageData.map((album) =>
          album.id === id ? { ...album, images: imageUrls } : album
        )
      );
      setSuccessMessage("Album images updated successfully!");
      setTimeout(() => setSuccessMessage(""), 3000);
    }
  };

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold mb-6">Image Management</h2>

      {/* Success Message */}
      {successMessage && (
        <div className="fixed top-4 right-4 bg-green-500 text-white p-4 rounded-md shadow-lg z-50">
          {successMessage}
        </div>
      )}

      {/* Delete Confirmation */}
      {deleteConfirmation && (
        <div className="fixed top-4 left-4 bg-red-500 text-white p-4 rounded-md shadow-lg z-50">
          {deleteConfirmation}
        </div>
      )}

      {/* Image Upload Section */}
      <div className="mb-4 flex items-center">
        <select
          value={newAlbumId}
          onChange={(e) => setNewAlbumId(e.target.value)}
          className="border border-gray-400 p-2 rounded-md mr-4"
        >
          <option value="">Select Album ID</option>
          {[1, 2, 3, 4, 5, 6].map((id) => (
            <option key={id} value={id}>
              {id}
            </option>
          ))}
        </select>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageUpload}
          multiple
          className="border border-gray-400 p-2 rounded-md mr-4"
        />
        <button
          onClick={handleAddImages}
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Add Images
        </button>
      </div>

      {/* Image Table */}
      <div>
        <table className="w-full table-auto bg-white text-left">
          <thead className="bg-orange-400 text-white">
            <tr>
              <th className="p-4">Album ID</th>
              <th className="p-4">Images Preview</th>
              <th className="p-4">Actions</th>
            </tr>
          </thead>
          <tbody>
            {imageData.map((album) => (
              <tr key={album.id} className="border-b border-gray-200">
                <td className="p-4">{album.id}</td>
                <td className="p-4">
                  <div className="flex space-x-2">
                    {album.images.map((url, index) => (
                      <img
                        key={index}
                        src={url}
                        alt={`Image ${album.id} - ${index}`}
                        className="w-16 h-16 object-cover"
                      />
                    ))}
                  </div>
                </td>
                <td className="p-4 flex items-center space-x-2">
                  <button
                    onClick={() => handleEditAlbum(album.id)}
                    className="bg-blue-500 text-white px-2 py-1 rounded-md hover:bg-blue-600"
                  >
                    Edit Album
                  </button>
                  <button
                    onClick={() => handleDeleteAlbum(album.id)}
                    className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600"
                  >
                    Delete Album
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ImageManagement;
