import React, { useState } from "react";
import { Link } from "react-router-dom";

const VideoManagement = () => {
  const [videoData, setVideoData] = useState([
    { id: 1, title: "Video 1", youtubeLink: "https://www.youtube.com/watch?v=xxx" },
    { id: 2, title: "Video 2", youtubeLink: "https://www.youtube.com/watch?v=yyy" },
    { id: 3, title: "Video 3", youtubeLink: "https://www.youtube.com/watch?v=zzz" },
    { id: 4, title: "Video 4", youtubeLink: "https://www.youtube.com/watch?v=aaa" },
    { id: 5, title: "Video 5", youtubeLink: "https://www.youtube.com/watch?v=bbb" },
    { id: 6, title: "Video 6", youtubeLink: "https://www.youtube.com/watch?v=ccc" },
  ]);

  const [editingVideoId, setEditingVideoId] = useState(null);
  const [newValues, setNewValues] = useState({ title: "", youtubeLink: "" });
  const [message, setMessage] = useState("");
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [videoToDelete, setVideoToDelete] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  const videosPerPage = 3;
  const totalPages = Math.ceil(videoData.length / videosPerPage);

  // Handle edit click: allow editing in the existing fields
  const handleEditClick = (video) => {
    setEditingVideoId(video.id);
    setNewValues({ title: video.title, youtubeLink: video.youtubeLink });
  };

  // Update the edited video
  const handleUpdateVideo = () => {
    setVideoData(
      videoData.map((video) =>
        video.id === editingVideoId
          ? { ...video, title: newValues.title, youtubeLink: newValues.youtubeLink }
          : video
      )
    );
    setMessage("Video updated successfully.");
    setTimeout(() => setMessage(""), 3000);
    setEditingVideoId(null); // Exit edit mode
  };

  // Cancel editing
  const handleCancelEdit = () => {
    setEditingVideoId(null); // Exit edit mode without saving changes
  };

  // Trigger delete confirmation
  const handleDeleteVideo = (video) => {
    setVideoToDelete(video);
    setShowDeleteModal(true);
  };

  // Confirm deletion
  const confirmDeleteVideo = () => {
    setVideoData(videoData.filter((video) => video.id !== videoToDelete.id));
    setShowDeleteModal(false);
    setMessage("Video deleted successfully.");
    setTimeout(() => setMessage(""), 3000);
  };

  // Cancel deletion
  const cancelDelete = () => {
    setShowDeleteModal(false);
    setVideoToDelete(null);
  };

  // Pagination logic
  const indexOfLastVideo = currentPage * videosPerPage;
  const indexOfFirstVideo = indexOfLastVideo - videosPerPage;
  const filteredVideos = videoData.filter(
    (video) =>
      video.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      video.id.toString().includes(searchTerm)
  );
  const currentVideos = filteredVideos.slice(indexOfFirstVideo, indexOfLastVideo);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Video Management</h2>

      {/* Confirmation Message */}
      {message && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-green-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-lg font-bold mb-4">{message}</h3>
          </div>
        </div>
      )}

      {/* Search Bar */}
      <div className="flex justify-end mb-4">
        <input
          type="text"
          placeholder="Search by ID or Title"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="p-2 rounded-md border border-gray-400 w-60"
        />
      </div>

      {/* Video Table */}
      <table className="w-full table-auto text-left mt-4 bg-white rounded-lg shadow-md">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-4">ID</th>
            <th className="p-4">Title</th>
            <th className="p-4">YouTube Link</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentVideos.length === 0 ? (
            <tr>
              <td colSpan="4" className="p-4 text-center text-gray-500">
                No videos found.
              </td>
            </tr>
          ) : (
            currentVideos.map((video) => (
              <tr
                key={video.id}
                className="hover:bg-gray-100 transition-colors border-b border-gray-300"
              >
                <td className="p-4">{video.id}</td>
                <td className="p-4">
                  {editingVideoId === video.id ? (
                    <input
                      type="text"
                      value={newValues.title}
                      onChange={(e) =>
                        setNewValues({ ...newValues, title: e.target.value })
                      }
                      className="p-2 rounded-md border border-gray-400 w-full"
                    />
                  ) : (
                    video.title
                  )}
                </td>
                <td className="p-4">
                  {editingVideoId === video.id ? (
                    <input
                      type="text"
                      value={newValues.youtubeLink}
                      onChange={(e) =>
                        setNewValues({ ...newValues, youtubeLink: e.target.value })
                      }
                      className="p-2 rounded-md border border-gray-400 w-full"
                    />
                  ) : (
                    video.youtubeLink
                  )}
                </td>
                <td className="p-4">
                  {editingVideoId === video.id ? (
                    <div className="flex space-x-2">
                      <button
                        onClick={handleUpdateVideo}
                        className="bg-green-400 text-white px-4 py-2 rounded-md"
                      >
                        Save
                      </button>
                      <button
                        onClick={handleCancelEdit}
                        className="bg-gray-400 text-white px-4 py-2 rounded-md"
                      >
                        Cancel
                      </button>
                    </div>
                  ) : (
                    <div className="flex space-x-2">
                      <button
                        onClick={() => handleEditClick(video)}
                        className="bg-green-400 text-white px-2 py-1 rounded-md"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteVideo(video)}
                        className="bg-red-400 text-white px-2 py-1 rounded-md"
                      >
                        Delete
                      </button>
                    </div>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="mt-4 flex justify-center items-center gap-4">
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => prev - 1)}
        >
          Previous
        </button>
        <span className="text-black">
          Page {currentPage} of {totalPages}
        </span>
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage((prev) => prev + 1)}
        >
          Next
        </button>
      </div>

      {/* Dashboard Button */}
      <div className="mt-6 flex justify-start">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Dashboard
        </Link>
      </div>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-gray-400 p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold mb-4">Confirm Deletion</h3>
            <p>Are you sure you want to delete this video?</p>
            <div className="mt-4 flex justify-end space-x-2">
              <button
                onClick={confirmDeleteVideo}
                className="bg-red-400 text-white px-4 py-2 rounded-md"
              >
                Yes, Delete
              </button>
              <button
                onClick={cancelDelete}
                className="bg-gray-400 text-white px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VideoManagement;
