const EventManagement = () => {
  const [eventData, setEventData] = useState([
    { id: 1, name: "Event 1", description: "Event description for Event 1" },
    { id: 2, name: "Event 2", description: "Event description for Event 2" },
    { id: 3, name: "Event 3", description: "Event description for Event 3" },
  ]);

  const [newEvent, setNewEvent] = useState({ id: "", name: "", description: "" });
  const [editEvent, setEditEvent] = useState(null);
  const [errorMessage, setErrorMessage] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const handleCreateEvent = () => {
    // Check for empty fields
    if (!newEvent.id || !newEvent.name || !newEvent.description) {
      setErrorMessage("Please fill all fields, including Event ID.");
      setTimeout(() => setErrorMessage(""), 3000);
      return;
    }

    // Ensure the ID is unique
    if (eventData.some((event) => event.id === Number(newEvent.id))) {
      setErrorMessage("Event ID must be unique.");
      setTimeout(() => setErrorMessage(""), 3000);
      return;
    }

    setEventData([...eventData, { ...newEvent, id: Number(newEvent.id) }]);
    setNewEvent({ id: "", name: "", description: "" });
    setSuccessMessage("Event added successfully.");
    setTimeout(() => setSuccessMessage(""), 3000);
  };

  const handleEditEvent = (event) => {
    setEditEvent(event);
    setNewEvent({ id: event.id, name: event.name, description: event.description });
  };

  const handleSaveEdit = () => {
    // Check for empty fields
    if (!newEvent.id || !newEvent.name || !newEvent.description) {
      setErrorMessage("Please fill all fields, including Event ID.");
      setTimeout(() => setErrorMessage(""), 3000);
      return;
    }

    // Ensure the ID is unique (if changed)
    if (
      newEvent.id !== editEvent.id &&
      eventData.some((event) => event.id === Number(newEvent.id))
    ) {
      setErrorMessage("Event ID must be unique.");
      setTimeout(() => setErrorMessage(""), 3000);
      return;
    }

    setEventData((prevData) =>
      prevData.map((event) =>
        event.id === editEvent.id ? { ...event, ...newEvent, id: Number(newEvent.id) } : event
      )
    );
    setNewEvent({ id: "", name: "", description: "" });
    setEditEvent(null);
    setSuccessMessage("Event updated successfully.");
    setTimeout(() => setSuccessMessage(""), 3000);
  };

  const handleCancelEdit = () => {
    setEditEvent(null);
    setNewEvent({ id: "", name: "", description: "" });
  };

  return (
    <div className="flex">
      {/* Sidebar and other components remain unchanged */}
      <div className="min-h-screen bg-gray-300 p-6 flex flex-col w-full ml-[240px]">
        <h2 className="text-2xl font-bold text-left mb-4">Event Management</h2>

        {/* Success and Error Messages */}
        {successMessage && <div className="bg-green-500 p-4 rounded-md text-white">{successMessage}</div>}
        {errorMessage && <div className="bg-red-500 p-4 rounded-md text-white">{errorMessage}</div>}

        {/* Input Form */}
        <div className="mb-3 grid grid-cols-1 sm:grid-cols-3 gap-4">
          <input
            type="number"
            placeholder="Event ID"
            value={newEvent.id}
            onChange={(e) => setNewEvent({ ...newEvent, id: e.target.value })}
            className="p-2 text-sm rounded-md border border-gray-400 w-full"
          />
          <input
            type="text"
            placeholder="Event Name"
            value={newEvent.name}
            onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
            className="p-2 text-sm rounded-md border border-gray-400 w-full"
          />
          <input
            type="text"
            placeholder="Description"
            value={newEvent.description}
            onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
            className="p-2 text-sm rounded-md border border-gray-400 w-full"
          />
          <div className="flex gap-2 justify-end">
            <button
              onClick={editEvent ? handleSaveEdit : handleCreateEvent}
              className="bg-orange-400 text-white rounded-md text-base text-center px-3 py-2"
            >
              {editEvent ? "Save" : "Add"}
            </button>
            {editEvent && (
              <button
                onClick={handleCancelEdit}
                className="bg-gray-500 text-white rounded-md text-base text-center px-3 py-2 w-60"
              >
                Cancel
              </button>
            )}
          </div>
        </div>

        {/* Event Table */}
        <table className="w-full table-auto text-left mt-4 bg-white rounded-md shadow-md text-base">
          <thead className="bg-orange-400 text-white">
            <tr>
              <th className="p-2">ID</th>
              <th className="p-2">Name</th>
              <th className="p-2">Description</th>
              <th className="p-2">Actions</th>
            </tr>
          </thead>
          <tbody>
            {eventData.map((event) => (
              <tr key={event.id} className="border-b">
                <td className="p-2">{event.id}</td>
                <td className="p-2">{event.name}</td>
                <td className="p-2">{event.description}</td>
                <td className="p-2 flex gap-2">
                  <button
                    onClick={() => handleEditEvent(event)}
                    className="bg-blue-500 text-white rounded-md text-base px-3 py-1"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => setEventData(eventData.filter((e) => e.id !== event.id))}
                    className="bg-red-500 text-white rounded-md text-base px-3 py-1"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default EventManagement;
