import React, { useState } from "react";

const ImageManagement = () => {
  const [imageData, setImageData] = useState([]);
  const [newImages, setNewImages] = useState([]);
  const [selectedImages, setSelectedImages] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");
  const [editingField, setEditingField] = useState({ id: null, field: "", value: "" });
  const [currentAlbumId, setCurrentAlbumId] = useState(1);

  // Handle image file upload
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const imageFiles = files.map((file) => ({
      id: Date.now() + Math.random(), // Unique ID
      albumId: null, // Assigned upon adding to the imageData
      title: file.name,
      url: URL.createObjectURL(file), // Temporary URL for preview
    }));
    setNewImages([...newImages, ...imageFiles]);
  };

  // Add selected images to imageData as a new album
  const handleAddImages = () => {
    if (selectedImages.length > 0) {
      const albumId = currentAlbumId; // Assign the current album ID
      const imagesWithAlbum = selectedImages.map((image) => ({
        ...image,
        albumId, // Add album ID
        id: Date.now() + Math.random(), // Generate a new unique ID
      }));

      setImageData([...imageData, ...imagesWithAlbum]);
      setSelectedImages([]);
      setNewImages([]);
      setSuccessMessage(`Album #${albumId} created with ${imagesWithAlbum.length} image(s)!`);
      setTimeout(() => setSuccessMessage(""), 3000); // Hide success message after 3 seconds
      setCurrentAlbumId(albumId + 1); // Increment album ID for the next batch
    } else {
      alert("Please select at least one image to create an album.");
    }
  };

  // Select/Unselect images for upload
  const handleSelectImage = (image) => {
    if (selectedImages.some((img) => img.id === image.id)) {
      setSelectedImages(selectedImages.filter((img) => img.id !== image.id));
    } else {
      setSelectedImages([...selectedImages, image]);
    }
  };

  // Start editing a specific field
  const startEditingField = (id, field, value) => {
    setEditingField({ id, field, value });
  };

  // Save updated field
  const handleSaveField = () => {
    if (editingField.value.trim()) {
      setImageData(
        imageData.map((image) =>
          image.id === editingField.id
            ? { ...image, [editingField.field]: editingField.value }
            : image
        )
      );
      setEditingField({ id: null, field: "", value: "" }); // Clear editing state
    } else {
      alert("Field value cannot be empty.");
    }
  };

  // Delete image
  const handleDeleteImage = (id) => {
    setImageData(imageData.filter((img) => img.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Image Management with Albums</h2>

      {/* Success Message */}
      {successMessage && (
        <div className="bg-green-100 text-green-800 p-4 rounded-md mb-4 text-center">
          {successMessage}
        </div>
      )}

      {/* Image Upload Section */}
      <div className="mb-6">
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageUpload}
          className="block mb-4"
        />
      </div>

      {/* Preview Uploaded Images */}
      {newImages.length > 0 && (
        <div className="grid grid-cols-4 gap-4 mb-6">
          {newImages.map((image) => (
            <div
              key={image.id}
              className={`p-2 border rounded-md cursor-pointer ${
                selectedImages.some((img) => img.id === image.id) ? "bg-orange-200" : "bg-white"
              }`}
              onClick={() => handleSelectImage(image)}
            >
              <img src={image.url} alt={image.title} className="w-full h-32 object-cover mb-2" />
              <div className="text-sm text-center font-semibold">{image.title}</div>
            </div>
          ))}
        </div>
      )}

      {/* Add Selected Images as Album Button */}
      {newImages.length > 0 && (
        <div className="mb-4">
          <button
            onClick={handleAddImages}
            className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
          >
            Create Album
          </button>
        </div>
      )}

      {/* Image Data Table by Albums */}
      <div>
        {Array.from(new Set(imageData.map((img) => img.albumId))).map((albumId) => (
          <div key={albumId} className="mb-6">
            <h3 className="text-lg font-bold mb-2">
              Album #{albumId}
            </h3>
            <table className="w-full table-auto text-left">
              <thead className="bg-orange-400 text-white">
                <tr>
                  <th className="p-4">ID</th>
                  <th className="p-4">Title</th>
                  <th className="p-4">Preview</th>
                  <th className="p-4">Actions</th>
                </tr>
              </thead>
              <tbody>
                {imageData
                  .filter((img) => img.albumId === albumId)
                  .map((image) => (
                    <tr key={image.id}>
                      <td className="p-4">{image.id}</td>

                      {/* Editable Title */}
                      <td className="p-4">
                        {editingField.id === image.id && editingField.field === "title" ? (
                          <input
                            type="text"
                            value={editingField.value}
                            onChange={(e) =>
                              setEditingField({ ...editingField, value: e.target.value })
                            }
                            className="border border-gray-400 rounded-md p-1"
                          />
                        ) : (
                          image.title
                        )}
                      </td>

                      {/* Image Preview */}
                      <td className="p-4">
                        <img src={image.url} alt={image.title} className="w-16 h-16 object-cover" />
                      </td>

                      {/* Actions */}
                      <td className="p-4 flex gap-2">
                        {editingField.id === image.id ? (
                          <button
                            onClick={handleSaveField}
                            className="bg-green-500 text-white px-2 py-1 rounded-md hover:bg-green-600"
                          >
                            Save
                          </button>
                        ) : (
                          <button
                            onClick={() => startEditingField(image.id, "title", image.title)}
                            className="bg-blue-500 text-white px-2 py-1 rounded-md hover:bg-blue-600"
                          >
                            Edit Title
                          </button>
                        )}
                        <button
                          onClick={() => handleDeleteImage(image.id)}
                          className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600"
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
              </tbody>
            </table>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ImageManagement;
