import React, { useState } from "react";
import { Link } from "react-router-dom";
import Sidebar from "./sidebar"; // Assuming Sidebar is in the same directory

const ImageManagement = () => {
  const [imageData, setImageData] = useState([
    { id: 1, url: "https://via.placeholder.com/150" },
    { id: 2, url: "https://via.placeholder.com/150" },
    { id: 3, url: "https://via.placeholder.com/150" },
    { id: 4, url: "https://via.placeholder.com/150" },
    { id: 5, url: "https://via.placeholder.com/150" },
  ]);

  const [newImages, setNewImages] = useState([]);
  const [selectedImages, setSelectedImages] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [deleteConfirmation, setDeleteConfirmation] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const [isEditModalOpen, setIsEditModalOpen] = useState(false);
  const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
  const [imageToEdit, setImageToEdit] = useState(null);
  const [imageToDelete, setImageToDelete] = useState(null);
  const [editedImage, setEditedImage] = useState("");

  // Handle image file upload
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const uploadedImages = files.map((file) => ({
      id: Date.now() + Math.random(),
      url: URL.createObjectURL(file),
    }));

    setNewImages((prev) => [...prev, ...uploadedImages]);
  };

  // Select or unselect images for upload
  const handleSelectImage = (image) => {
    if (selectedImages.some((img) => img.id === image.id)) {
      setSelectedImages((prev) => prev.filter((img) => img.id !== image.id));
    } else {
      setSelectedImages((prev) => [...prev, image]);
    }
  };

  // Add selected images to the main image data
  const handleAddImages = () => {
    if (selectedImages.length > 0) {
      setImageData((prev) => [...prev, ...selectedImages]);
      setSelectedImages([]);
      setNewImages([]);
      setSuccessMessage("Images added successfully!");
      setTimeout(() => setSuccessMessage(""), 3000);
    } else {
      setErrorMessage("Please select at least one image to upload.");
      setTimeout(() => setErrorMessage(""), 3000);
    }
  };

  // Open edit modal
  const handleEditImage = (image) => {
    setImageToEdit(image);
    setIsEditModalOpen(true);
  };

  // Handle save for editing
  const handleSaveEdit = () => {
    if (editedImage) {
      setImageData((prev) =>
        prev.map((img) =>
          img.id === imageToEdit.id ? { ...img, url: editedImage } : img
        )
      );
      setSuccessMessage("Image updated successfully!");
      setTimeout(() => setSuccessMessage(""), 3000);
    }
    setIsEditModalOpen(false);
    setEditedImage("");
  };

  // Cancel edit modal
  const handleCancelEdit = () => {
    setIsEditModalOpen(false);
    setEditedImage("");
  };

  // Open delete confirmation modal
  const handleDeleteImage = (image) => {
    setImageToDelete(image);
    setIsDeleteModalOpen(true);
  };

  // Confirm deletion
  const handleConfirmDelete = () => {
    setImageData((prev) =>
      prev.filter((img) => img.id !== imageToDelete.id)
    );
    setIsDeleteModalOpen(false);
    setDeleteConfirmation("Image deleted successfully.");
    setTimeout(() => setDeleteConfirmation(""), 3000);
  };

  // Cancel delete action
  const handleCancelDelete = () => {
    setIsDeleteModalOpen(false);
    setImageToDelete(null);
  };

  // Filter images by search query
  const filteredImages = imageData.filter((img) =>
    img.id.toString().includes(searchQuery)
  );

  // Paginate images
  const paginatedImages = filteredImages.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const totalPages = Math.ceil(filteredImages.length / itemsPerPage);

  return (
    <div className="flex">
      <Sidebar />

      <div className="min-h-screen bg-gray-300 p-6 w-full ml-[250px]">
        <h2 className="text-2xl font-bold mb-6">Image Management</h2>

        {successMessage && (
          <div className="fixed top-4 right-4 bg-green-500 text-white p-4 rounded-md">
            {successMessage}
          </div>
        )}
        {errorMessage && (
          <div className="fixed top-4 left-4 bg-red-500 text-white p-4 rounded-md">
            {errorMessage}
          </div>
        )}
        {deleteConfirmation && (
          <div className="fixed top-4 right-4 bg-green-500 text-white p-4 rounded-md">
            {deleteConfirmation}
          </div>
        )}

        <div className="mb-4 flex items-center">
          <input
            type="file"
            multiple
            accept="image/*"
            onChange={handleImageUpload}
            className="border p-2 rounded-md mr-4"
          />
          <button
            onClick={handleAddImages}
            className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500"
          >
            Add Images
          </button>
        </div>

        <div className="mb-4">
          <input
            type="text"
            placeholder="Search by ID"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="border p-2 rounded-md w-full"
          />
        </div>

        <table className="w-full bg-white">
          <thead className="bg-orange-400 text-white">
            <tr>
              <th className="p-4">ID</th>
              <th className="p-4">Preview</th>
              <th className="p-4">Actions</th>
            </tr>
          </thead>
          <tbody>
            {paginatedImages.map((image) => (
              <tr key={image.id}>
                <td className="p-4">{image.id}</td>
                <td className="p-4">
                  <img
                    src={image.url}
                    alt="preview"
                    className="w-16 h-16 object-cover"
                  />
                </td>
                <td className="p-4">
                  <button
                    onClick={() => handleEditImage(image)}
                    className="bg-blue-500 text-white px-2 py-1 rounded-md mr-2"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteImage(image)}
                    className="bg-red-500 text-white px-2 py-1 rounded-md"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>

        <div className="mt-4 flex justify-between">
          <button
            disabled={currentPage === 1}
            onClick={() => setCurrentPage((prev) => prev - 1)}
            className={`px-4 py-2 rounded-md ${
              currentPage === 1
                ? "bg-gray-400 text-white"
                : "bg-orange-400 text-white hover:bg-orange-500"
            }`}
          >
            Previous
          </button>
          <span>
            Page {currentPage} of {totalPages}
          </span>
          <button
            disabled={currentPage === totalPages}
            onClick={() => setCurrentPage((prev) => prev + 1)}
            className={`px-4 py-2 rounded-md ${
              currentPage === totalPages
                ? "bg-gray-400 text-white"
                : "bg-orange-400 text-white hover:bg-orange-500"
            }`}
          >
            Next
          </button>
        </div>

        {isEditModalOpen && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded-md">
              <h3 className="text-xl mb-4">Edit Image</h3>
              <input
                type="file"
                onChange={(e) =>
                  setEditedImage(URL.createObjectURL(e.target.files[0]))
                }
                className="border p-2 mb-4"
              />
              <button
                onClick={handleSaveEdit}
                className="bg-green-500 text-white px-4 py-2 rounded-md mr-4"
              >
                Save
              </button>
              <button
                onClick={handleCancelEdit}
                className="bg-gray-500 text-white px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        )}

        {isDeleteModalOpen && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-white p-6 rounded-md">
              <h3 className="text-xl mb-4">Are you sure you want to delete?</h3>
              <button
                onClick={handleConfirmDelete}
                className="bg-red-500 text-white px-4 py-2 rounded-md mr-4"
              >
                Yes
              </button>
              <button
                onClick={handleCancelDelete}
                className="bg-gray-500 text-white px-4 py-2 rounded-md"
              >
                No
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ImageManagement;
