import React, { useState } from "react";
import { Link } from "react-router-dom";

const VideoManagement = () => {
  const [videoData, setVideoData] = useState([
    { id: 1, title: "Video 1", youtubeLink: "https://www.youtube.com/watch?v=xxx" },
    { id: 2, title: "Video 2", youtubeLink: "https://www.youtube.com/watch?v=yyy" },
    { id: 3, title: "Video 3", youtubeLink: "https://www.youtube.com/watch?v=zzz" },
  ]);

  const [newVideo, setNewVideo] = useState({ title: "", youtubeLink: "" });
  const [message, setMessage] = useState("");
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [videoToDelete, setVideoToDelete] = useState(null);
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [videoToUpdate, setVideoToUpdate] = useState(null);
  const [editedVideo, setEditedVideo] = useState({ title: "", youtubeLink: "" });

  // Add new video
  const handleCreateVideo = () => {
    if (newVideo.title && newVideo.youtubeLink) {
      setVideoData([...videoData, { id: videoData.length + 1, ...newVideo }]);
      setNewVideo({ title: "", youtubeLink: "" });
      setMessage("Video added successfully.");
    } else {
      alert("Please fill all fields for the video.");
    }
  };

  // Delete functionality
  const handleDeleteVideo = (video) => {
    setVideoToDelete(video);
    setShowDeleteModal(true);
  };

  const confirmDeleteVideo = () => {
    setVideoData(videoData.filter((video) => video.id !== videoToDelete.id));
    setShowDeleteModal(false);
    setMessage("Video deleted successfully.");
  };

  const cancelDelete = () => {
    setShowDeleteModal(false);
    setVideoToDelete(null);
  };

  // Update functionality
  const handleEditVideo = (video) => {
    setVideoToUpdate(video);
    setEditedVideo({ title: video.title, youtubeLink: video.youtubeLink });
    setShowUpdateModal(true);
  };

  const confirmUpdateVideo = () => {
    if (editedVideo.title && editedVideo.youtubeLink) {
      setVideoData(
        videoData.map((video) =>
          video.id === videoToUpdate.id ? { ...video, ...editedVideo } : video
        )
      );
      setShowUpdateModal(false);
      setMessage("Video updated successfully.");
    } else {
      alert("Please fill all fields to update the video.");
    }
  };

  const cancelUpdate = () => {
    setShowUpdateModal(false);
    setEditedVideo({ title: "", youtubeLink: "" });
    setVideoToUpdate(null);
  };

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Video Management</h2>
      {message && <div className="bg-green-100 text-green-800 p-4 rounded-md mb-4">{message}</div>}

      {/* Add New Video */}
      <div className="mb-4 grid grid-cols-3 gap-2">
        <input
          type="text"
          placeholder="Title"
          value={newVideo.title}
          onChange={(e) => setNewVideo({ ...newVideo, title: e.target.value })}
          className="p-2 rounded-md border border-gray-400"
        />
        <input
          type="text"
          placeholder="YouTube Link"
          value={newVideo.youtubeLink}
          onChange={(e) => setNewVideo({ ...newVideo, youtubeLink: e.target.value })}
          className="p-2 rounded-md border border-gray-400"
        />
        <button onClick={handleCreateVideo} className="bg-orange-400 text-white px-4 py-2 rounded-md">
          Add Video
        </button>
      </div>

      {/* Video Table */}
      <table className="w-full table-auto text-left mt-4">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-4">ID</th>
            <th className="p-4">Title</th>
            <th className="p-4">YouTube Link</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {videoData.map((video) => (
            <tr key={video.id}>
              <td className="p-4">{video.id}</td>
              <td className="p-4">{video.title}</td>
              <td className="p-4">{video.youtubeLink}</td>
              <td className="p-4">
                <button
                  onClick={() => handleEditVideo(video)}
                  className="bg-green-600 text-white px-2 py-1 rounded-md mr-2"
                >
                  Update
                </button>
                <button
                  onClick={() => handleDeleteVideo(video)}
                  className="bg-red-600 text-white px-2 py-1 rounded-md"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-red-600 p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold mb-4">Confirm Deletion</h3>
            <p className="text-white">
              Are you sure you want to delete "{videoToDelete?.title}"?
            </p>
            <div className="flex justify-end gap-4 mt-4">
              <button
                onClick={confirmDeleteVideo}
                className="bg-white text-red-600 px-4 py-2 rounded-md"
              >
                Delete
              </button>
              <button
                onClick={cancelDelete}
                className="bg-gray-400 text-white px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Update Confirmation Modal */}
      {showUpdateModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-green-600 p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold mb-4 text-white">Confirm Update</h3>
            <div className="mb-4">
              <input
                type="text"
                value={editedVideo.title}
                onChange={(e) => setEditedVideo({ ...editedVideo, title: e.target.value })}
                placeholder="Video Title"
                className="p-2 rounded-md w-full mb-2"
              />
              <input
                type="text"
                value={editedVideo.youtubeLink}
                onChange={(e) => setEditedVideo({ ...editedVideo, youtubeLink: e.target.value })}
                placeholder="YouTube Link"
                className="p-2 rounded-md w-full"
              />
            </div>
            <div className="flex justify-end gap-4">
              <button
                onClick={confirmUpdateVideo}
                className="bg-white text-green-600 px-4 py-2 rounded-md"
              >
                Update
              </button>
              <button
                onClick={cancelUpdate}
                className="bg-gray-400 text-white px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VideoManagement;
