import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const PlaceManagement = () => {
  const [places, setPlaces] = useState([]);
  const [search, setSearch] = useState("");
  const [formData, setFormData] = useState({
    date: "",
    description: "",
    image: "",
    place: "",
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [showModal, setShowModal] = useState(false); 
  const [modalMessage, setModalMessage] = useState(""); 
  const [modalType, setModalType] = useState(""); 

  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [videoToDelete, setVideoToDelete] = useState(null); 

  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); 

  const [showAddCard, setShowAddCard] = useState(false);

  const [selectedPlace, setSelectedPlace] = useState(null); // New state for selected place details

  const [successMessage, setSuccessMessage] = useState(""); // Success message state

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prevData) => ({ ...prevData, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAdd = () => {
    const { date, description, image, place } = formData;
    if (!date || !description || !place || !image) {
      setModalMessage("Date, Description, Place, and Image are required.");
      setModalType("error");
      setShowModal(true);
      return;
    }
    setPlaces([...places, formData]);
    setFormData({ date: "", description: "", image: "", place: "" });
    setShowAddCard(false);
    setSuccessMessage("Place added successfully!");
    setShowModal(true);
  };

  const handleEdit = (index) => {
    const isConfirmed = window.confirm("Are you sure you want to edit this place?");
    if (isConfirmed) {
      setFormData(places[index]);
      setEditingIndex(index);
      setShowAddCard(true);
    }
  };

  const handleUpdate = () => {
    if (editingIndex !== null) {
      const updatedPlaces = [...places];
      updatedPlaces[editingIndex] = formData;
      setPlaces(updatedPlaces);
      setEditingIndex(null);
      setFormData({ date: "", description: "", image: "", place: "" });
      setShowAddCard(false);
      setSuccessMessage("Place updated successfully!");
      setShowModal(true);
    }
  };

  const handleDelete = (index) => {
    setVideoToDelete(places[index]);
    setShowDeleteModal(true);
  };

  const confirmDeleteVideo = () => {
    const updatedPlaces = places.filter((_, i) => i !== places.indexOf(videoToDelete));
    setPlaces(updatedPlaces);
    setShowDeleteModal(false);
    setSuccessMessage("Place deleted successfully!");
    setShowModal(true);
  };

  const cancelDelete = () => {
    setShowDeleteModal(false);
  };

  const filteredPlaces = places.filter(
    ({ date, description, place }) =>
      date.toLowerCase().includes(search.toLowerCase()) ||
      description.toLowerCase().includes(search.toLowerCase()) ||
      place.toLowerCase().includes(search.toLowerCase())
  );

  const totalPages = Math.ceil(filteredPlaces.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredPlaces.slice(indexOfFirstItem, indexOfLastItem);

  // Function to open the selected place details
  const handlePlaceClick = (place) => {
    setSelectedPlace(place); // Set the selected place to show in the card
  };

  // Function to close the place details card
  const handleClosePlaceCard = () => {
    setSelectedPlace(null); // Reset the selected place data
  };

  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage(""); // Clear success message after 3 seconds
      }, 3000); // 3000ms = 3 seconds

      return () => clearTimeout(timer); // Clean up the timer when component unmounts
    }
  }, [successMessage]);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4 text-black">Place management</h1>

      {/* Modal */}
      {showModal && (
        <div
          className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center"
          onClick={() => setShowModal(false)}
        >
          <div
            className="bg-white p-6 rounded shadow-md w-80"
            onClick={(e) => e.stopPropagation()}
          >
            <p className={`text-center ${modalType === "success" ? "text-green-500" : "text-red-500"}`}>
              {modalMessage}
            </p>
            <div className="mt-4 text-center">
              <button
                onClick={() => setShowModal(false)}
                className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-gray-200 p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold mb-4">Confirm Deletion</h3>
            <p>Are you sure you want to delete "{videoToDelete?.place}"?</p>
            <div className="flex justify-end gap-4 mt-4">
              <button
                onClick={confirmDeleteVideo}
                className="bg-red-600 text-white px-4 py-2 rounded-md"
              >
                Delete
              </button>
              <button
                onClick={cancelDelete}
                className=" text-gray-500 px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Success Message with transition */}
      {successMessage && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center transition-all duration-300 opacity-100">
          <div className="bg-green-500 p-6 rounded-lg shadow-lg text-white max-w-sm w-full transition-all duration-300">
            <h3 className="text-lg font-bold mb-4">{successMessage}</h3>
          </div>
        </div>
      )}

      {/* Search Bar */}
      <div className="flex justify-end mb-6">
        <input
          placeholder="Search..."
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="border border-gray-400 rounded p-2 w-64"
        />
      </div>

      {/* Add Button */}
      <div className="mb-6 flex justify-end">
        <button
          onClick={() => setShowAddCard((prevState) => !prevState)} // Toggle card visibility
          className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-green-500"
        >
          {showAddCard ? "Cancel" : "Add"}
        </button>
      </div>

      {/* Add Place Form Card */}
      {showAddCard && (
        <div className="mb-6 bg-gray-200 p-6 rounded shadow-md w-full max-w-lg mx-auto">
          <h2 className="text-xl font-semibold mb-4">{editingIndex !== null ? "Edit Place" : "Add Place"}</h2>

          <input
            placeholder="Date"
            type="date"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            placeholder="Description"
            type="text"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            placeholder="Place"
            type="text"
            name="place"
            value={formData.place}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />
          <input
            type="file"
            onChange={handleImageChange}
            className="border border-gray-400 rounded p-2 w-full mb-4"
          />

          <div className="flex justify-end">
            <button
              onClick={editingIndex !== null ? handleUpdate : handleAdd}
              className="bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600"
            >
              {editingIndex !== null ? "Update" : "Add"}
            </button>
          </div>
        </div>
      )}

      {/* Place List */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {currentItems.map((place, index) => (
          <div
            key={index}
            className="border border-gray-400 rounded p-4 bg-gray-100"
            onClick={() => handlePlaceClick(place)} // Open place details on click
          >
            <h3 className="text-xl font-semibold">{place.place}</h3>
            <p>{place.description}</p>
          </div>
        ))}
      </div>

      {/* Pagination */}
      <div className="mt-6 flex justify-center">
        <button
          onClick={() => setCurrentPage(currentPage - 1)}
          disabled={currentPage === 1}
          className="bg-gray-300 px-4 py-2 rounded-md mx-2"
        >
          Prev
        </button>
        <button
          onClick={() => setCurrentPage(currentPage + 1)}
          disabled={currentPage === totalPages}
          className="bg-gray-300 px-4 py-2 rounded-md mx-2"
        >
          Next
        </button>
      </div>

      {/* Place Details Modal */}
      {selectedPlace && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-6 rounded shadow-md w-full max-w-lg">
            <h3 className="text-xl font-semibold mb-4">Place Details</h3>
            <p><strong>Place:</strong> {selectedPlace.place}</p>
            <p><strong>Description:</strong> {selectedPlace.description}</p>
            <p><strong>Date:</strong> {selectedPlace.date}</p>
            <div className="flex justify-end mt-4">
              <button
                onClick={handleClosePlaceCard}
                className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default PlaceManagement;
