import React, { useState } from "react";
import { Link } from "react-router-dom";

/**
 * ImageManagement Component
 * Provides a user interface to manage images with functionality to upload, edit, delete,
 * search, paginate, and categorize images into albums.
 */
const ImageManagement = () => {
  // State variables for managing data and interactions
  const [imageData, setImageData] = useState([]); // Stores the list of all images
  const [newImages, setNewImages] = useState([]); // Temporarily stores newly uploaded images
  const [albumName, setAlbumName] = useState(""); // Stores the current album name being created
  const [selectedImages, setSelectedImages] = useState([]); // Tracks selected images for album creation
  const [searchQuery, setSearchQuery] = useState(""); // Stores the current search query
  const [currentAlbumId, setCurrentAlbumId] = useState(1); // Tracks the ID for new albums
  const [successMessage, setSuccessMessage] = useState(""); // Displays success messages
  const [currentPage, setCurrentPage] = useState(1); // Tracks the current page for pagination
  const itemsPerPage = 5; // Number of items displayed per page

  // State for editing functionality
  const [editingImageId, setEditingImageId] = useState(null); // Tracks the image being edited
  const [editedTitle, setEditedTitle] = useState(""); // Stores the updated title during edit
  const [editedImage, setEditedImage] = useState(null); // Stores the updated image during edit

  /**
   * Handles image uploads and adds them to the newImages state.
   */
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const imageFiles = files.map((file) => ({
      id: Date.now() + Math.random(),
      albumId: null,
      albumName: "",
      title: file.name,
      url: URL.createObjectURL(file),
    }));
    setNewImages([...newImages, ...imageFiles]);
  };

  /**
   * Adds selected images to a new album with the provided album name.
   */
  const handleAddImages = () => {
    if (!albumName.trim()) {
      alert("Please provide an album name.");
      return;
    }

    if (selectedImages.length > 0) {
      const albumId = currentAlbumId;
      const imagesWithAlbum = selectedImages.map((image) => ({
        ...image,
        albumId,
        albumName,
        id: Date.now() + Math.random(),
      }));

      setImageData([...imageData, ...imagesWithAlbum]);
      setSelectedImages([]);
      setNewImages([]);
      setAlbumName("");
      setSuccessMessage(`Album "${albumName}" (#${albumId}) created successfully!`);
      setTimeout(() => setSuccessMessage(""), 3000);
      setCurrentAlbumId(albumId + 1);
    } else {
      alert("Please select at least one image to create an album.");
    }
  };

  /**
   * Toggles the selection state of an image.
   */
  const handleSelectImage = (image) => {
    if (selectedImages.some((img) => img.id === image.id)) {
      setSelectedImages(selectedImages.filter((img) => img.id !== image.id));
    } else {
      setSelectedImages([...selectedImages, image]);
    }
  };

  /**
   * Deletes an image by its ID.
   */
  const handleDeleteImage = (id) => {
    setImageData(imageData.filter((img) => img.id !== id));
  };

  /**
   * Initiates the editing process for an image.
   */
  const handleEditImage = (image) => {
    setEditingImageId(image.id);
    setEditedTitle(image.title);
    setEditedImage(image.url);
  };

  /**
   * Saves the edited details of an image.
   */
  const handleSaveEdit = () => {
    if (editedImage) {
      setImageData(
        imageData.map((img) =>
          img.id === editingImageId
            ? { ...img, title: editedTitle, url: editedImage }
            : img
        )
      );
    } else {
      setImageData(
        imageData.map((img) =>
          img.id === editingImageId ? { ...img, title: editedTitle } : img
        )
      );
    }
    setEditingImageId(null);
    setEditedTitle("");
    setEditedImage(null);
  };

  /**
   * Cancels the editing process.
   */
  const handleCancelEdit = () => {
    setEditingImageId(null);
    setEditedTitle("");
    setEditedImage(null);
  };

  // Filters images based on the search query.
  const filteredImages = imageData.filter(
    (img) =>
      img.albumName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      img.albumId?.toString().includes(searchQuery)
  );

  // Applies pagination to the images displayed.
  const paginatedImages = [...newImages, ...filteredImages].slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage
  );

  const totalPages = Math.ceil((newImages.length + filteredImages.length) / itemsPerPage);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Image Management with Table</h2>

      {/** Success message display */}
      {successMessage && (
        <div className="fixed top-4 right-4 bg-green-500 text-white p-4 rounded-md shadow-lg z-50">
          {successMessage}
        </div>
      )}

      {/** Input fields for album name and image upload */}
      <div className="mb-4">
        <input
          type="text"
          placeholder="Enter Album Name"
          value={albumName}
          onChange={(e) => setAlbumName(e.target.value)}
          className="block mb-4 border border-gray-400 p-2 rounded-md w-full"
        />
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageUpload}
          className="block mb-4"
        />
        <button
          onClick={handleAddImages}
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Create Album
        </button>
      </div>

      {/** Search bar for filtering images */}
      <div className="mb-6">
        <input
          type="text"
          placeholder="Search by Album Name or ID"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          className="border border-orange-400 p-2 rounded-md w-full"
        />
      </div>

      {/** Table displaying image details */}
      <table className="w-full border border-collapse">
        <thead>
          <tr className="bg-orange-400 text-white">
            <th className="border px-4 py-2">ID</th>
            <th className="border px-4 py-2">Title</th>
            <th className="border px-4 py-2">Preview</th>
            <th className="border px-4 py-2">Album Name</th>
            <th className="border px-4 py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {paginatedImages.map((image) => (
            <tr key={image.id} className="bg-gray-100">
              <td className="border px-4 py-2">{image.id}</td>
              <td className="border px-4 py-2">
                {editingImageId === image.id ? (
                  <input
                    type="text"
                    value={editedTitle}
                    onChange={(e) => setEditedTitle(e.target.value)}
                    className="border p-2 w-full rounded-md"
                  />
                ) : (
                  image.title
                )}
              </td>
              <td className="border px-4 py-2">
                {editingImageId === image.id ? (
                  <input
                    type="file"
                    onChange={(e) => setEditedImage(URL.createObjectURL(e.target.files[0]))}
                    className="p-2 w-full rounded-md"
                  />
                ) : (
                  <img
                    src={image.url}
                    alt={image.title}
                    className="w-16 h-16 object-cover"
                  />
                )}
              </td>
              <td className="border px-4 py-2">{image.albumName || "Uncategorized"}</td>
              <td className="border px-4 py-2">
                {editingImageId === image.id ? (
                  <>
                    <button
                      onClick={handleSaveEdit}
                      className="bg-green-500 text-white px-2 py-1 rounded-md hover:bg-green-600 mr-2"
                    >
                      Save
                    </button>
                    <button
                      onClick={handleCancelEdit}
                      className="bg-gray-500 text-white px-2 py-1 rounded-md hover:bg-gray-600"
                    >
                      Cancel
                    </button>
                  </>
                ) : (
                  <>
                    <button
                      onClick={() => handleEditImage(image)}
                      className="bg-blue-500 text-white px-2 py-1 rounded-md hover:bg-blue-600 mr-2"
                    >
                      Edit
                    </button>
                    <button
                      onClick={() => handleDeleteImage(image.id)}
                      className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600 mr-2"
                    >
                      Delete
                    </button>
                    {/* Add this button for adding images to albums */}
                    <button
                      onClick={() => handleSelectImage(image)}
                      className="bg-yellow-500 text-white px-2 py-1 rounded-md hover:bg-yellow-600"
                    >
                      Add to Album
                    </button>
                  </>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/** Pagination controls */}
      {totalPages > 1 && (
        <div className="flex justify-center mt-6">
          {[...Array(totalPages)].map((_, index) => (
            <button
              key={index}
              onClick={() => setCurrentPage(index + 1)}
              className={`px-4 py-2 mx-1 border rounded-md ${
                currentPage === index + 1 ? "bg-orange-400 text-white" : "bg-white"
              }`}
            >
              {index + 1}
            </button>
          ))}
        </div>
      )}

      {/** Link to Dashboard */}
      <div className="mt-6 flex justify-left">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Dashboard
        </Link>
      </div>
    </div>
  );
};

export default ImageManagement;
