import React, { useState } from "react";
import { Link } from "react-router-dom";

// Event Management Component
const EventManagement = () => {
  // State for managing events, new event inputs, edit event, messages, search term, pagination, and delete confirmation
  const [eventData, setEventData] = useState([
    { id: 1, name: "Event 1", description: "Event description" },
    { id: 2, name: "Event 2", description: "Event description" },
    { id: 3, name: "Event 3", description: "Event description" },
  ]);

  const [newEvent, setNewEvent] = useState({ name: "", description: "" });
  const [editEvent, setEditEvent] = useState(null);
  const [message, setMessage] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [videoToDelete, setVideoToDelete] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const eventsPerPage = 5;

  // Display success or error messages temporarily
  const showMessage = (msg) => {
    setMessage(msg);
    setTimeout(() => setMessage(""), 3000); // Clears the message after 3 seconds
  };

  // Add a new event to the list
  const handleCreateEvent = () => {
    if (newEvent.name && newEvent.description) {
      setEventData([...eventData, { id: eventData.length + 1, ...newEvent }]);
      setNewEvent({ name: "", description: "" });
      showMessage("Event added successfully.");
    } else {
      alert("Please fill all fields for the event.");
    }
  };

  // Edit the selected event
  const handleEditEvent = (event) => {
    setEditEvent(event);
    setNewEvent({ name: event.name, description: event.description });
  };

  // Save edited event directly in the table row
  const handleSaveEdit = () => {
    if (newEvent.name && newEvent.description && editEvent) {
      setEventData((prevData) =>
        prevData.map((event) =>
          event.id === editEvent.id ? { ...event, ...newEvent } : event
        )
      );
      setNewEvent({ name: "", description: "" });
      showMessage("Event updated successfully.");
      setEditEvent(null);
    } else {
      alert("Please fill all fields for the event.");
    }
  };

  // Cancel editing event
  const handleCancelEdit = () => {
    setEditEvent(null);
    setNewEvent({ name: "", description: "" });
  };

  // Delete selected event (trigger confirmation)
  const handleDeleteEvent = (event) => {
    setVideoToDelete(event);
    setShowDeleteModal(true);
  };

  // Confirm event deletion
  const confirmDeleteEvent = () => {
    setEventData(eventData.filter((event) => event.id !== videoToDelete.id));
    setShowDeleteModal(false);
    showMessage("Event deleted successfully.");
  };

  // Cancel event deletion
  const cancelDelete = () => {
    setShowDeleteModal(false);
    setVideoToDelete(null);
  };

  // Filter events based on search term
  const filteredEvents = eventData.filter((event) =>
    event.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredEvents.length / eventsPerPage);
  const currentEvents = filteredEvents.slice(
    (currentPage - 1) * eventsPerPage,
    currentPage * eventsPerPage
  );

  return (
    <div className="min-h-screen bg-gray-300 p-6 flex flex-col">
      <h2 className="text-2xl font-bold text-center mb-4">Event Management</h2>

      {/* Success message display */}
      {message && (
        <div className="bg-green-800 text-white p-2 rounded-md mb-3 text-sm">
          {message}
        </div>
      )}

      {/* Search functionality */}
<div className="mb-3 flex justify-end">
  <input
    type="text"
    placeholder="Search Events"
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
    className="p-2 text-base rounded-md border border-gray-400 w-1/1 max-w-sm"
  />
</div>


      {/* New Event Form */}
      <div className="mb-3 grid grid-cols-3 gap-2">
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
          className="p-2 text-base rounded-md border border-gray-400"
        />
        <input
          type="text"
          placeholder="Description"
          value={newEvent.description}
          onChange={(e) =>
            setNewEvent({ ...newEvent, description: e.target.value })
          }
          className="p-2 text-base rounded-md border border-gray-400"
        />
        <button
          onClick={editEvent ? handleSaveEdit : handleCreateEvent}
          className="bg-orange-400 text-white rounded-md text-base px-3 py-2"
        >
          {editEvent ? "Save" : "Add"}
        </button>
        {editEvent && (
          <button
            onClick={handleCancelEdit}
            className="bg-gray-500 text-white rounded-md text-base px-3 py-2 wd-30"
          >
            Cancel
          </button>
        )}
      </div>

      {/* Events Table */}
      <table className="w-full table-auto text-left mt-4 bg-white rounded-md shadow-md text-base">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-2">ID</th>
            <th className="p-2">Name</th>
            <th className="p-2">Description</th>
            <th className="p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentEvents.map((event, index) => (
            <tr
              key={event.id}
              className={`${
                index % 2 === 0 ? "bg-white" : "bg-gray-100"
              } border-b`}
            >
              <td className="p-2">{event.id}</td>
              <td className="p-2">{event.name}</td>
              <td className="p-2">{event.description}</td>
              <td className="p-2 flex gap-2">
                <button
                  onClick={() => handleEditEvent(event)}
                  className="bg-blue-500 text-white rounded-md text-base px-3 py-1"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteEvent(event)}
                  className="bg-red-500 text-white rounded-md text-base px-3 py-1"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination Controls */}
      <div className="mt-3 flex justify-center items-center gap-3 text-base">
        <button
          className={`px-3 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => prev - 1)}
        >
          Previous
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button
          className={`px-3 py-2 rounded-md ${
            currentPage === totalPages
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage((prev) => prev + 1)}
        >
          Next
        </button>
      </div>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-md shadow-md">
            <h3 className="text-xl">Are you sure you want to delete this event?</h3>
            <div className="mt-4 flex gap-2">
              <button
                onClick={confirmDeleteEvent}
                className="bg-red-500 text-white rounded-md px-4 py-2"
              >
                Yes, Delete
              </button>
              <button
                onClick={cancelDelete}
                className="bg-gray-500 text-white rounded-md px-4 py-2"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Link to Dashboard */}
      <div className="mt-auto flex justify-left">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-3 py-2 rounded-md hover:bg-orange-500 text-base"
        >
          Dashboard
        </Link>
      </div>
    </div>
  );
};

export default EventManagement;
