import React, { useState } from "react";
import { Link } from "react-router-dom"; // Import Link for navigation

const EventManagement = () => {
  const [eventData, setEventData] = useState([
    { id: 1, name: "Event 1", description: "Event description" },
    { id: 2, name: "Event 2", description: "Event description" },
  ]);

  const [newEvent, setNewEvent] = useState({ name: "", description: "" });
  const [editEvent, setEditEvent] = useState(null);
  const [message, setMessage] = useState("");
  const [searchTerm, setSearchTerm] = useState(""); // For search functionality

  // Handle creating a new event
  const handleCreateEvent = () => {
    if (newEvent.name && newEvent.description) {
      setEventData([...eventData, { id: eventData.length + 1, ...newEvent }]);
      setNewEvent({ name: "", description: "" });
      setMessage("Event added successfully.");
    } else {
      alert("Please fill all fields for the event.");
    }
  };

  // Handle editing an event
  const handleEditEvent = (event) => {
    setEditEvent(event); // Set the selected event for editing
    setNewEvent({ name: event.name, description: event.description }); // Pre-fill form fields for editing
  };

  // Handle saving the edited event
  const handleSaveEdit = () => {
    if (newEvent.name && newEvent.description) {
      setEventData(
        eventData.map((event) =>
          event.id === editEvent.id ? { ...event, ...newEvent } : event
        )
      );
      setNewEvent({ name: "", description: "" });
      setMessage("Event updated successfully.");
      setEditEvent(null); // Reset the edit state
    } else {
      alert("Please fill all fields for the event.");
    }
  };

  // Handle deleting an event
  const handleDeleteEvent = (id) => {
    setEventData(eventData.filter((event) => event.id !== id));
    setMessage("Event deleted successfully.");
  };

  // Filter events by name (search)
  const filteredEvents = eventData.filter((event) =>
    event.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      {/* Dashboard Button */}
      <div className="mt-6 flex justify-start">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Dashboard
        </Link>
      </div>

      {/* Page Heading */}
      <h2 className="text-2xl font-bold text-center mb-6">Event Management</h2>

      {/* Success Message */}
      {message && (
        <div className="bg-green-100 text-green-800 p-4 rounded-md mb-4">
          {message}
        </div>
      )}

     {/* Search Bar */}
<div className="mb-4 flex justify-end">
  <input
    type="text"
    placeholder="Search Events"
    value={searchTerm}
    onChange={(e) => setSearchTerm(e.target.value)}
    className="p-2 rounded-md border border-gray-400 w-2/1" // Set the width to 1/3
  />
</div>



      {/* Input Fields for New Event / Edit Event */}
      <div className="mb-4 grid grid-cols-2 gap-2">
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
          className="p-2 rounded-md border border-gray-400"
        />
        <input
          type="text"
          placeholder="Description"
          value={newEvent.description}
          onChange={(e) =>
            setNewEvent({ ...newEvent, description: e.target.value })
          }
          className="p-2 rounded-md border border-gray-400"
        />
        <button
          onClick={editEvent ? handleSaveEdit : handleCreateEvent}
          className="bg-orange-400 text-white px-4 py-2 rounded-md"
        >
          {editEvent ? "Save Changes" : "Add Event"}
        </button>
      </div>

      {/* Event Table */}
      <table className="w-full table-auto text-left mt-4">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-4">ID</th>
            <th className="p-4">Name</th>
            <th className="p-4">Description</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredEvents.map((event) => (
            <tr key={event.id}>
              <td className="p-4">{event.id}</td>
              <td className="p-4">{event.name}</td>
              <td className="p-4">{event.description}</td>
              <td className="p-4">
                <button
                  onClick={() => handleEditEvent(event)}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteEvent(event.id)}
                  className="bg-red-500 text-white px-4 py-2 rounded-md"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EventManagement;
