import React, { useState } from "react";
import { Link } from "react-router-dom";

const ManagePortfolio = () => {
  // State for image and video data
  const [imageData, setImageData] = useState([
    { id: 1, eventId: 1, title: "Image 1", url: "https://via.placeholder.com/150", description: "Image description" },
    { id: 2, eventId: 2, title: "Image 2", url: "https://via.placeholder.com/150", description: "Image description" },
  ]);

  const [videoData, setVideoData] = useState([
    { id: 1, title: "Video 1", youtubeLink: "https://www.youtube.com/watch?v=xxx" },
    { id: 2, title: "Video 2", youtubeLink: "https://www.youtube.com/watch?v=xxx" },
  ]);

  const [currentImagePage, setCurrentImagePage] = useState(1);
  const [currentVideoPage, setCurrentVideoPage] = useState(1);
  const [newImage, setNewImage] = useState({ eventId: "", title: "", url: "" });
  const [newVideo, setNewVideo] = useState({ title: "", youtubeLink: "" });

  const itemsPerPage = 5;

  // Pagination logic for images
  const indexOfLastImage = currentImagePage * itemsPerPage;
  const indexOfFirstImage = indexOfLastImage - itemsPerPage;
  const currentImages = imageData.slice(indexOfFirstImage, indexOfLastImage);

  // Pagination logic for videos
  const indexOfLastVideo = currentVideoPage * itemsPerPage;
  const indexOfFirstVideo = indexOfLastVideo - itemsPerPage;
  const currentVideos = videoData.slice(indexOfFirstVideo, indexOfLastVideo);

  // Handlers for adding, updating, and deleting images and videos
  const handleCreateImage = () => {
    if (newImage.eventId && newImage.title && newImage.url) {
      setImageData([
        ...imageData,
        { id: imageData.length + 1, ...newImage },
      ]);
      setNewImage({ eventId: "", title: "", url: "" });
    }
  };

  const handleCreateVideo = () => {
    if (newVideo.title && newVideo.youtubeLink) {
      setVideoData([
        ...videoData,
        { id: videoData.length + 1, ...newVideo },
      ]);
      setNewVideo({ title: "", youtubeLink: "" });
    }
  };

  const handleDeleteImage = (id) => {
    setImageData(imageData.filter((image) => image.id !== id));
  };

  const handleDeleteVideo = (id) => {
    setVideoData(videoData.filter((video) => video.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      {/* Navigation */}
      <div className="mb-6">
        <Link
          to="/dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Go to Dashboard
        </Link>
      </div>

      {/* Main Heading */}
      <h2 className="text-2xl font-bold mb-6 text-center">Manage Portfolio</h2>

      {/* Flexbox layout for Image and Video Management */}
      <div className="flex gap-6">
        {/* Image Management Section */}
        <div className="w-1/2 bg-white p-4 rounded-md shadow">
          <h3 className="text-xl font-semibold mb-4 text-center">Image Management</h3>

          {/* Add New Image Form */}
          <div className="mb-4">
            <input
              type="text"
              placeholder="Event ID"
              value={newImage.eventId}
              onChange={(e) => setNewImage({ ...newImage, eventId: e.target.value })}
              className="p-2 mr-2 rounded-md border border-gray-400"
            />
            <input
              type="text"
              placeholder="Title"
              value={newImage.title}
              onChange={(e) => setNewImage({ ...newImage, title: e.target.value })}
              className="p-2 mr-2 rounded-md border border-gray-400"
            />
            <input
              type="text"
              placeholder="Image URL"
              value={newImage.url}
              onChange={(e) => setNewImage({ ...newImage, url: e.target.value })}
              className="p-2 mr-2 rounded-md border border-gray-400"
            />
            <button
              onClick={handleCreateImage}
              className="bg-orange-400 text-white px-4 py-2 rounded-md"
            >
              Add Image
            </button>
          </div>

          {/* Image Table */}
          <table className="w-full table-auto text-left mt-4">
            <thead className="bg-orange-400 text-white">
              <tr>
                <th className="p-4">ID</th>
                <th className="p-4">Event ID</th>
                <th className="p-4">Title</th>
                <th className="p-4">URL</th>
                <th className="p-4">Actions</th>
              </tr>
            </thead>
            <tbody>
              {currentImages.map((image) => (
                <tr key={image.id}>
                  <td className="p-4">{image.id}</td>
                  <td className="p-4">{image.eventId}</td>
                  <td className="p-4">{image.title}</td>
                  <td className="p-4">{image.url}</td>
                  <td className="p-4">
                    <button
                      className="bg-red-500 text-white px-4 py-2 rounded-md"
                      onClick={() => handleDeleteImage(image.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          <div className="flex justify-between mt-4">
            <button
              onClick={() => setCurrentImagePage(currentImagePage - 1)}
              disabled={currentImagePage === 1}
              className="bg-gray-400 text-white px-4 py-2 rounded-md"
            >
              Previous
            </button>
            <button
              onClick={() => setCurrentImagePage(currentImagePage + 1)}
              disabled={currentImagePage * itemsPerPage >= imageData.length}
              className="bg-gray-400 text-white px-4 py-2 rounded-md"
            >
              Next
            </button>
          </div>
        </div>

        {/* Video Management Section */}
        <div className="w-1/2 bg-white p-4 rounded-md shadow">
          <h3 className="text-xl font-semibold mb-4 text-center">Video Management</h3>

          {/* Add New Video Form */}
          <div className="mb-4">
            <input
              type="text"
              placeholder="Title"
              value={newVideo.title}
              onChange={(e) => setNewVideo({ ...newVideo, title: e.target.value })}
              className="p-2 mr-2 rounded-md border border-gray-400"
            />
            <input
              type="text"
              placeholder="YouTube Link"
              value={newVideo.youtubeLink}
              onChange={(e) => setNewVideo({ ...newVideo, youtubeLink: e.target.value })}
              className="p-2 mr-2 rounded-md border border-gray-400"
            />
            <button
              onClick={handleCreateVideo}
              className="bg-orange-400 text-white px-4 py-2 rounded-md"
            >
              Add Video
            </button>
          </div>

          {/* Video Table */}
          <table className="w-full table-auto text-left mt-4">
            <thead className="bg-orange-400 text-white">
              <tr>
                <th className="p-4">ID</th>
                <th className="p-4">Title</th>
                <th className="p-4">YouTube Link</th>
                <th className="p-4">Actions</th>
              </tr>
            </thead>
            <tbody>
              {currentVideos.map((video) => (
                <tr key={video.id}>
                  <td className="p-4">{video.id}</td>
                  <td className="p-4">{video.title}</td>
                  <td className="p-4">{video.youtubeLink}</td>
                  <td className="p-4">
                    <button
                      className="bg-red-500 text-white px-4 py-2 rounded-md"
                      onClick={() => handleDeleteVideo(video.id)}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {/* Pagination */}
          <div className="flex justify-between mt-4">
            <button
              onClick={() => setCurrentVideoPage(currentVideoPage - 1)}
              disabled={currentVideoPage === 1}
              className="bg-gray-400 text-white px-4 py-2 rounded-md"
            >
              Previous
            </button>
            <button
              onClick={() => setCurrentVideoPage(currentVideoPage + 1)}
              disabled={currentVideoPage * itemsPerPage >= videoData.length}
              className="bg-gray-400 text-white px-4 py-2 rounded-md"
            >
              Next
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ManagePortfolio;
