import React, { useState } from "react";

const ImageManagement = () => {
  const [imageData, setImageData] = useState([
    { id: 1, images: ["https://via.placeholder.com/150"] },
    { id: 2, images: ["https://via.placeholder.com/150"] },
    { id: 3, images: ["https://via.placeholder.com/150"] },
  ]);

  const [editAlbumId, setEditAlbumId] = useState(null);
  const [selectedImages, setSelectedImages] = useState([]);
  const [newImages, setNewImages] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");

  // Start editing an album
  const handleEditAlbum = (id) => {
    setEditAlbumId(id);
    const album = imageData.find((album) => album.id === id);
    setSelectedImages(album ? album.images : []);
    setNewImages([]);
  };

  // Delete a specific image from the album
  const handleDeleteImage = (index) => {
    const updatedImages = selectedImages.filter((_, i) => i !== index);
    setSelectedImages(updatedImages);
  };

  // Add new images to the album
  const handleAddNewImages = (e) => {
    const files = Array.from(e.target.files);
    const fileUrls = files.map((file) => URL.createObjectURL(file));
    setNewImages([...newImages, ...fileUrls]);
  };

  // Save the updated album
  const handleSaveChanges = () => {
    const updatedAlbums = imageData.map((album) =>
      album.id === editAlbumId
        ? { ...album, images: [...selectedImages, ...newImages] }
        : album
    );

    setImageData(updatedAlbums);
    setEditAlbumId(null);
    setSelectedImages([]);
    setNewImages([]);
    setSuccessMessage("Album updated successfully!");
    setTimeout(() => setSuccessMessage(""), 3000);
  };

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold mb-6">Image Management</h2>

      {/* Success Message */}
      {successMessage && (
        <div className="fixed top-4 right-4 bg-green-500 text-white p-4 rounded-md shadow-lg z-50">
          {successMessage}
        </div>
      )}

      {/* Album List */}
      <table className="w-full table-auto bg-white text-left">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-4">Album ID</th>
            <th className="p-4">Images Preview</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {imageData.map((album) => (
            <tr key={album.id} className="border-b border-gray-200">
              <td className="p-4">{album.id}</td>
              <td className="p-4">
                <div className="flex space-x-2">
                  {album.images.map((url, index) => (
                    <img
                      key={index}
                      src={url}
                      alt={`Album ${album.id} - Image ${index}`}
                      className="w-16 h-16 object-cover"
                    />
                  ))}
                </div>
              </td>
              <td className="p-4">
                <button
                  onClick={() => handleEditAlbum(album.id)}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
                >
                  Edit
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Edit Album */}
      {editAlbumId && (
        <div className="mt-6 bg-white p-6 rounded-md shadow-md">
          <h3 className="text-xl font-bold mb-4">Editing Album ID: {editAlbumId}</h3>

          {/* Current Images */}
          <div className="mb-4">
            <h4 className="font-semibold mb-2">Current Images:</h4>
            <div className="flex space-x-4">
              {selectedImages.map((url, index) => (
                <div key={index} className="relative">
                  <img
                    src={url}
                    alt={`Selected ${index}`}
                    className="w-20 h-20 object-cover border"
                  />
                  <button
                    onClick={() => handleDeleteImage(index)}
                    className="absolute top-0 right-0 bg-red-500 text-white p-1 rounded-full"
                  >
                    X
                  </button>
                </div>
              ))}
            </div>
          </div>

          {/* Add New Images */}
          <div className="mb-4">
            <h4 className="font-semibold mb-2">Add New Images:</h4>
            <input
              type="file"
              accept="image/*"
              multiple
              onChange={handleAddNewImages}
              className="border p-2 rounded-md"
            />
            <div className="flex space-x-4 mt-4">
              {newImages.map((url, index) => (
                <img
                  key={index}
                  src={url}
                  alt={`New ${index}`}
                  className="w-20 h-20 object-cover border"
                />
              ))}
            </div>
          </div>

          {/* Save Changes */}
          <button
            onClick={handleSaveChanges}
            className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
          >
            Save Changes
          </button>
        </div>
      )}
    </div>
  );
};

export default ImageManagement;
