import React, { useState } from "react";
import { Link } from "react-router-dom";

const VideoManagement = () => {
  const [videoData, setVideoData] = useState([
    { id: 1, title: "Video 1", youtubeLink: "https://www.youtube.com/watch?v=xxx" },
    { id: 2, title: "Video 2", youtubeLink: "https://www.youtube.com/watch?v=yyy" },
    { id: 3, title: "Video 3", youtubeLink: "https://www.youtube.com/watch?v=zzz" },
    { id: 4, title: "Video 4", youtubeLink: "https://www.youtube.com/watch?v=aaa" },
    { id: 5, title: "Video 5", youtubeLink: "https://www.youtube.com/watch?v=bbb" },
    { id: 6, title: "Video 6", youtubeLink: "https://www.youtube.com/watch?v=ccc" },
  ]);

  const [newVideo, setNewVideo] = useState({ title: "", youtubeLink: "" });
  const [message, setMessage] = useState("");
  const [editingVideoId, setEditingVideoId] = useState(null);
  const [editedVideo, setEditedVideo] = useState({ title: "", youtubeLink: "" });

  const [currentPage, setCurrentPage] = useState(1);
  const videosPerPage = 3;
  const totalPages = Math.ceil(videoData.length / videosPerPage);

  const handleCreateVideo = () => {
    if (newVideo.title && newVideo.youtubeLink) {
      setVideoData([...videoData, { id: videoData.length + 1, ...newVideo }]);
      setNewVideo({ title: "", youtubeLink: "" });
      setMessage("Video added successfully.");
    } else {
      alert("Please fill all fields for the video.");
    }
  };

  const handleEditVideo = (video) => {
    setEditingVideoId(video.id);
    setEditedVideo({ title: video.title, youtubeLink: video.youtubeLink });
  };

  const handleUpdateVideo = () => {
    if (editedVideo.title && editedVideo.youtubeLink) {
      setVideoData(
        videoData.map((video) =>
          video.id === editingVideoId ? { ...video, ...editedVideo } : video
        )
      );
      setEditingVideoId(null);
      setEditedVideo({ title: "", youtubeLink: "" });
      setMessage("Video updated successfully.");
    } else {
      alert("Please fill all fields to update the video.");
    }
  };

  const handleCancelEdit = () => {
    setEditingVideoId(null);
    setEditedVideo({ title: "", youtubeLink: "" });
  };

  const handleDeleteVideo = (id) => {
    setVideoData(videoData.filter((video) => video.id !== id));
    setMessage("Video deleted successfully.");
  };

  const indexOfLastVideo = currentPage * videosPerPage;
  const indexOfFirstVideo = indexOfLastVideo - videosPerPage;
  const currentVideos = videoData.slice(indexOfFirstVideo, indexOfLastVideo);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Video Management</h2>
      {message && <div className="bg-green-100 text-green-800 p-4 rounded-md mb-4">{message}</div>}

      {/* Add new video */}
      <div className="mb-4 grid grid-cols-3 gap-2">
        <input
          type="text"
          placeholder="Title"
          value={newVideo.title}
          onChange={(e) => setNewVideo({ ...newVideo, title: e.target.value })}
          className="p-1.5 text-sm rounded-md border border-gray-400"
        />
        <input
          type="text"
          placeholder="YouTube Link"
          value={newVideo.youtubeLink}
          onChange={(e) => setNewVideo({ ...newVideo, youtubeLink: e.target.value })}
          className="p-1.5 text-sm rounded-md border border-gray-400"
        />
        <button onClick={handleCreateVideo} className="bg-orange-400 text-white px-4 py-2 rounded-md">
          Add Video
        </button>
      </div>

      {/* Edit Video Form */}
      {editingVideoId && (
        <div className="mb-4">
          <h3 className="text-lg font-bold">Edit Video</h3>
          <input
            type="text"
            placeholder="Title"
            value={editedVideo.title}
            onChange={(e) => setEditedVideo({ ...editedVideo, title: e.target.value })}
            className="p-1.5 text-sm rounded-md border border-gray-400 mb-2"
          />
          <input
            type="text"
            placeholder="YouTube Link"
            value={editedVideo.youtubeLink}
            onChange={(e) => setEditedVideo({ ...editedVideo, youtubeLink: e.target.value })}
            className="p-1.5 text-sm rounded-md border border-gray-400 mb-2"
          />
          <button
            onClick={handleUpdateVideo}
            className="bg-green-400 text-white px-4 py-2 rounded-md mr-2"
          >
            Save Changes
          </button>
          <button
            onClick={handleCancelEdit}
            className="bg-gray-400 text-white px-4 py-2 rounded-md"
          >
            Cancel
          </button>
        </div>
      )}

      {/* Video Table */}
      <table className="w-full table-auto text-left mt-4">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-4">ID</th>
            <th className="p-4">Title</th>
            <th className="p-4">YouTube Link</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentVideos.map((video) => (
            <tr key={video.id}>
              <td className="p-4">{video.id}</td>
              <td className="p-4">{video.title}</td>
              <td className="p-4">{video.youtubeLink}</td>
              <td className="p-4">
                <button
                  onClick={() => handleEditVideo(video)}
                  className="bg-blue-400 text-white px-2 py-1 rounded-md mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDeleteVideo(video.id)}
                  className="bg-red-400 text-white px-2 py-1 rounded-md"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="mt-4 flex justify-center items-center gap-4">
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => prev - 1)}
        >
          Previous
        </button>
        <span className="text-black">
          Page {currentPage} of {totalPages}
        </span>
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage((prev) => prev + 1)}
        >
          Next
        </button>
      </div>

      {/* Dashboard Button */}
      <div className="mt-6 flex justify-start">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Dashboard
        </Link>
      </div>
    </div>
  );
};

export default VideoManagement;
