import React, { useState } from "react";

const ImageManagement = () => {
  const [imageData, setImageData] = useState([
    { id: 1, eventId: 1, title: "Image 1", url: "https://via.placeholder.com/150", description: "Image description" },
    { id: 2, eventId: 2, title: "Image 2", url: "https://via.placeholder.com/150", description: "Image description" },
  ]);

  const [newImages, setNewImages] = useState([]);
  const [selectedImages, setSelectedImages] = useState([]);
  const [successMessage, setSuccessMessage] = useState("");
  const [editingTitle, setEditingTitle] = useState({ id: null, newTitle: "" });

  // Handle image file upload
  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files); // Convert FileList to array
    const imageFiles = files.map((file) => ({
      id: Date.now() + Math.random(), // Generate unique ID
      eventId: "",
      title: file.name,
      url: URL.createObjectURL(file), // Temporary URL for preview
    }));
    setNewImages([...newImages, ...imageFiles]); // Add to new images
  };

  // Add selected images to imageData
  const handleAddImages = () => {
    if (selectedImages.length > 0) {
      setImageData([...imageData, ...selectedImages]);
      setSelectedImages([]);
      setNewImages([]);
      setSuccessMessage(`${selectedImages.length} images uploaded successfully!`);
      setTimeout(() => setSuccessMessage(""), 3000); // Hide success message after 3 seconds
    } else {
      alert("Please select at least one image to upload.");
    }
  };

  // Select/Unselect images for upload
  const handleSelectImage = (image) => {
    if (selectedImages.some((img) => img.id === image.id)) {
      setSelectedImages(selectedImages.filter((img) => img.id !== image.id));
    } else {
      setSelectedImages([...selectedImages, image]);
    }
  };

  // Start editing title
  const startEditingTitle = (id, currentTitle) => {
    setEditingTitle({ id, newTitle: currentTitle });
  };

  // Update the title
  const handleUpdateTitle = () => {
    if (editingTitle.newTitle.trim()) {
      setImageData(
        imageData.map((img) =>
          img.id === editingTitle.id ? { ...img, title: editingTitle.newTitle } : img
        )
      );
      setEditingTitle({ id: null, newTitle: "" }); // Clear editing state
    } else {
      alert("Title cannot be empty.");
    }
  };

  // Delete image
  const handleDeleteImage = (id) => {
    setImageData(imageData.filter((img) => img.id !== id));
  };

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Image Management</h2>

      {/* Success Message */}
      {successMessage && (
        <div className="bg-green-100 text-green-800 p-4 rounded-md mb-4 text-center">
          {successMessage}
        </div>
      )}

      {/* Image Upload Section */}
      <div className="mb-6">
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageUpload}
          className="block mb-4"
        />
      </div>

      {/* Preview Uploaded Images */}
      {newImages.length > 0 && (
        <div className="grid grid-cols-4 gap-4 mb-6">
          {newImages.map((image) => (
            <div
              key={image.id}
              className={`p-2 border rounded-md cursor-pointer ${
                selectedImages.some((img) => img.id === image.id) ? "bg-orange-200" : "bg-white"
              }`}
              onClick={() => handleSelectImage(image)}
            >
              <img src={image.url} alt={image.title} className="w-full h-32 object-cover mb-2" />
              <div className="text-sm text-center font-semibold">{image.title}</div>
            </div>
          ))}
        </div>
      )}

      {/* Add Selected Images Button */}
      {newImages.length > 0 && (
        <div className="mb-4">
          <button
            onClick={handleAddImages}
            className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
          >
            Add Selected Images
          </button>
        </div>
      )}

      {/* Image Data Table */}
      <table className="w-full table-auto text-left mt-4">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-4">ID</th>
            <th className="p-4">Event ID</th>
            <th className="p-4">Title</th>
            <th className="p-4">Preview</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {imageData.map((image) => (
            <tr key={image.id}>
              <td className="p-4">{image.id}</td>
              <td className="p-4">{image.eventId || "N/A"}</td>
              <td className="p-4">
                {editingTitle.id === image.id ? (
                  <input
                    type="text"
                    value={editingTitle.newTitle}
                    onChange={(e) =>
                      setEditingTitle({ ...editingTitle, newTitle: e.target.value })
                    }
                    className="border border-gray-400 rounded-md p-1"
                  />
                ) : (
                  image.title
                )}
              </td>
              <td className="p-4">
                <a href={image.url} target="_blank" rel="noopener noreferrer">
                  <img src={image.url} alt={image.title} className="w-16 h-16 object-cover" />
                </a>
              </td>
              <td className="p-4">
                {editingTitle.id === image.id ? (
                  <button
                    onClick={handleUpdateTitle}
                    className="bg-green-500 text-white px-2 py-1 rounded-md hover:bg-green-600 transition duration-300 mr-2"
                  >
                    Save Title
                  </button>
                ) : (
                  <button
                    onClick={() => startEditingTitle(image.id, image.title)}
                    className="bg-blue-500 text-white px-2 py-1 rounded-md hover:bg-blue-600 transition duration-300 mr-2"
                  >
                    Edit Title
                  </button>
                )}
                <button
                  onClick={() => handleDeleteImage(image.id)}
                  className="bg-red-500 text-white px-2 py-1 rounded-md hover:bg-red-600 transition duration-300"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ImageManagement;
