import React, { useState } from "react";
import { Link } from "react-router-dom";

const VideoManagement = () => {
  const [videoData, setVideoData] = useState([
    { id: 1, title: "Video 1", youtubeLink: "https://www.youtube.com/watch?v=xxx" },
    { id: 2, title: "Video 2", youtubeLink: "https://www.youtube.com/watch?v=yyy" },
    { id: 3, title: "Video 3", youtubeLink: "https://www.youtube.com/watch?v=zzz" },
  ]);

  const [newVideo, setNewVideo] = useState({ title: "", youtubeLink: "" });
  const [editingVideo, setEditingVideo] = useState(null); // Holds the video being edited
  const [message, setMessage] = useState("");
  const [searchTerm, setSearchTerm] = useState("");

  const videosPerPage = 3;
  const totalPages = Math.ceil(videoData.length / videosPerPage);
  const [currentPage, setCurrentPage] = useState(1);

  // Add New Video
  const handleCreateVideo = () => {
    if (newVideo.title && newVideo.youtubeLink) {
      setVideoData([...videoData, { id: videoData.length + 1, ...newVideo }]);
      setNewVideo({ title: "", youtubeLink: "" });
      setMessage("Video added successfully.");
      setTimeout(() => setMessage(""), 3000);
    } else {
      alert("Please fill all fields for the video.");
    }
  };

  // Edit Video (Load data into form)
  const handleEditVideo = (video) => {
    setEditingVideo(video); // Load selected video into form for editing
    setNewVideo({ title: video.title, youtubeLink: video.youtubeLink });
  };

  // Save Edited Video
  const handleUpdateVideo = () => {
    if (editingVideo && newVideo.title && newVideo.youtubeLink) {
      setVideoData((prevData) =>
        prevData.map((video) =>
          video.id === editingVideo.id
            ? { ...video, title: newVideo.title, youtubeLink: newVideo.youtubeLink }
            : video
        )
      );
      setEditingVideo(null); // Exit edit mode
      setNewVideo({ title: "", youtubeLink: "" });
      setMessage("Video updated successfully.");
      setTimeout(() => setMessage(""), 3000);
    } else {
      alert("Please fill all fields for the video.");
    }
  };

  // Cancel Editing
  const handleCancelEdit = () => {
    setEditingVideo(null);
    setNewVideo({ title: "", youtubeLink: "" });
  };

  // Delete Video
  const handleDeleteVideo = (id) => {
    setVideoData(videoData.filter((video) => video.id !== id));
    setMessage("Video deleted successfully.");
    setTimeout(() => setMessage(""), 3000);
  };

  // Pagination Logic
  const indexOfLastVideo = currentPage * videosPerPage;
  const indexOfFirstVideo = indexOfLastVideo - videosPerPage;
  const filteredVideos = videoData.filter(
    (video) =>
      video.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      video.id.toString().includes(searchTerm)
  );
  const currentVideos = filteredVideos.slice(indexOfFirstVideo, indexOfLastVideo);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Video Management</h2>

      {/* Notification Message */}
      {message && (
        <div className="bg-green-600 p-3 text-white rounded mb-4">
          <p>{message}</p>
        </div>
      )}

      {/* Search Bar */}
      <input
        type="text"
        placeholder="Search by ID or Title"
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        className="p-2 rounded border w-full mb-4"
      />

      {/* Add or Edit Video */}
      <div className="mb-4 flex gap-2">
        <input
          type="text"
          placeholder="Title"
          value={newVideo.title}
          onChange={(e) => setNewVideo({ ...newVideo, title: e.target.value })}
          className="p-2 rounded border w-1/3"
        />
        <input
          type="text"
          placeholder="YouTube Link"
          value={newVideo.youtubeLink}
          onChange={(e) => setNewVideo({ ...newVideo, youtubeLink: e.target.value })}
          className="p-2 rounded border w-1/3"
        />
        {editingVideo ? (
          <>
            <button
              onClick={handleUpdateVideo}
              className="bg-green-500 text-white p-2 rounded"
            >
              Save
            </button>
            <button
              onClick={handleCancelEdit}
              className="bg-gray-500 text-white p-2 rounded"
            >
              Cancel
            </button>
          </>
        ) : (
          <button
            onClick={handleCreateVideo}
            className="bg-blue-500 text-white p-2 rounded"
          >
            Add Video
          </button>
        )}
      </div>

      {/* Video Table */}
      <table className="w-full border-collapse bg-white">
        <thead>
          <tr className="bg-gray-800 text-white">
            <th className="p-4">ID</th>
            <th className="p-4">Title</th>
            <th className="p-4">YouTube Link</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentVideos.length > 0 ? (
            currentVideos.map((video) => (
              <tr key={video.id} className="border-b">
                <td className="p-4">{video.id}</td>
                <td className="p-4">{video.title}</td>
                <td className="p-4">
                  <a
                    href={video.youtubeLink}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-500 underline"
                  >
                    {video.youtubeLink}
                  </a>
                </td>
                <td className="p-4">
                  <button
                    onClick={() => handleEditVideo(video)}
                    className="bg-yellow-500 text-white p-2 rounded mr-2"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteVideo(video.id)}
                    className="bg-red-500 text-white p-2 rounded"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="4" className="p-4 text-center">
                No videos found.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default VideoManagement;
