import React, { useState } from "react";
import { Link } from "react-router-dom";

const VideoManagement = () => {
  const [videoData, setVideoData] = useState([
    { id: 1, title: "Video 1", youtubeLink: "https://www.youtube.com/watch?v=xxx" },
    { id: 2, title: "Video 2", youtubeLink: "https://www.youtube.com/watch?v=yyy" },
    { id: 3, title: "Video 3", youtubeLink: "https://www.youtube.com/watch?v=zzz" },
    { id: 4, title: "Video 4", youtubeLink: "https://www.youtube.com/watch?v=aaa" },
    { id: 5, title: "Video 5", youtubeLink: "https://www.youtube.com/watch?v=bbb" },
    { id: 6, title: "Video 6", youtubeLink: "https://www.youtube.com/watch?v=ccc" },
  ]);

  const [newVideo, setNewVideo] = useState({ title: "", youtubeLink: "" });
  const [message, setMessage] = useState("");
  const [editingVideoId, setEditingVideoId] = useState(null);
  const [editedVideoData, setEditedVideoData] = useState({ title: "", youtubeLink: "" }); // State for edited video data
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [videoToDelete, setVideoToDelete] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchTerm, setSearchTerm] = useState("");
  const videosPerPage = 3;
  const totalPages = Math.ceil(videoData.length / videosPerPage);

  // Create a new video
  const handleCreateVideo = () => {
    if (newVideo.title && newVideo.youtubeLink) {
      setVideoData([...videoData, { id: videoData.length + 1, ...newVideo }]);
      setNewVideo({ title: "", youtubeLink: "" });
      setMessage("Video added successfully.");
      setTimeout(() => setMessage(""), 3000);
    } else {
      alert("Please fill all fields for the video.");
    }
  };

  // Handle editing and save edited video data
  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setEditedVideoData({ ...editedVideoData, [name]: value });
  };

  const handleUpdateVideo = (id) => {
    setVideoData(
      videoData.map((video) =>
        video.id === id ? { ...video, ...editedVideoData } : video
      )
    );
    setEditingVideoId(null);
    setMessage("Video updated successfully.");
    setTimeout(() => setMessage(""), 3000);
  };

  // Cancel editing
  const handleCancelEdit = () => {
    setEditingVideoId(null);
  };

  // Trigger delete confirmation
  const handleDeleteVideo = (video) => {
    setVideoToDelete(video);
    setShowDeleteModal(true);
  };

  // Confirm deletion
  const confirmDeleteVideo = () => {
    setVideoData(videoData.filter((video) => video.id !== videoToDelete.id));
    setShowDeleteModal(false);
    setMessage("Video deleted successfully.");
    setTimeout(() => setMessage(""), 3000);
  };

  // Cancel deletion
  const cancelDelete = () => {
    setShowDeleteModal(false);
    setVideoToDelete(null);
  };

  // Pagination logic
  const indexOfLastVideo = currentPage * videosPerPage;
  const indexOfFirstVideo = indexOfLastVideo - videosPerPage;
  const filteredVideos = videoData.filter(
    (video) =>
      video.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      video.id.toString().includes(searchTerm)
  );
  const currentVideos = filteredVideos.slice(indexOfFirstVideo, indexOfLastVideo);

  return (
    <div className="min-h-screen bg-gray-300 p-6">
      <h2 className="text-2xl font-bold text-center mb-6">Video Management</h2>

      {/* Confirmation Message */}
      {message && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-green-600 p-6 rounded-lg shadow-lg text-white">
            <h3 className="text-lg font-bold mb-4">{message}</h3>
          </div>
        </div>
      )}

      {/* Search Bar */}
      <div className="flex justify-end mb-4">
        <input
          type="text"
          placeholder="Search by ID or Title"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="p-2 rounded-md border border-gray-400 w-60"
        />
      </div>

      {/* Add new video */}
      <div className="mb-4 grid grid-cols-3 gap-2">
        <input
          type="text"
          placeholder="Title"
          value={newVideo.title}
          onChange={(e) => setNewVideo({ ...newVideo, title: e.target.value })}
          className="p-2 rounded-md border border-gray-400"
        />
        <input
          type="text"
          placeholder="YouTube Link"
          value={newVideo.youtubeLink}
          onChange={(e) => setNewVideo({ ...newVideo, youtubeLink: e.target.value })}
          className="p-2 rounded-md border border-gray-400"
        />
        <button onClick={handleCreateVideo} className="bg-orange-400 text-white px-4 py-2 rounded-md">
          Add Video
        </button>
      </div>

      {/* Video Table */}
      <table className="w-full table-auto text-left mt-4 bg-white rounded-lg shadow-md">
        <thead className="bg-orange-400 text-white">
          <tr>
            <th className="p-4">ID</th>
            <th className="p-4">Title</th>
            <th className="p-4">YouTube Link</th>
            <th className="p-4">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentVideos.length === 0 ? (
            <tr>
              <td colSpan="4" className="p-4 text-center text-gray-500">
                No videos found.
              </td>
            </tr>
          ) : (
            currentVideos.map((video) => (
              <tr key={video.id} className="hover:bg-gray-100 transition-colors border-b border-gray-300">
                <td className="p-4">{video.id}</td>
                <td className="p-4">
                  {editingVideoId === video.id ? (
                    <input
                      type="text"
                      name="title"
                      value={editedVideoData.title}
                      onChange={handleEditChange}
                      className="p-2 rounded-md border border-gray-400"
                    />
                  ) : (
                    video.title
                  )}
                </td>
                <td className="p-4">
                  {editingVideoId === video.id ? (
                    <input
                      type="text"
                      name="youtubeLink"
                      value={editedVideoData.youtubeLink}
                      onChange={handleEditChange}
                      className="p-2 rounded-md border border-gray-400"
                    />
                  ) : (
                    video.youtubeLink
                  )}
                </td>
                <td className="p-4">
                  {editingVideoId === video.id ? (
                    <>
                      <button
                        onClick={() => handleUpdateVideo(video.id)}
                        className="bg-green-400 text-white px-2 py-1 rounded-md mr-2"
                      >
                        Save
                      </button>
                      <button
                        onClick={handleCancelEdit}
                        className="bg-gray-400 text-white px-2 py-1 rounded-md"
                      >
                        Cancel
                      </button>
                    </>
                  ) : (
                    <>
                      <button
                        onClick={() => {
                          setEditingVideoId(video.id);
                          setEditedVideoData({ title: video.title, youtubeLink: video.youtubeLink });
                        }}
                        className="bg-green-400 text-white px-2 py-1 rounded-md mr-2"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDeleteVideo(video)}
                        className="bg-red-400 text-white px-2 py-1 rounded-md"
                      >
                        Delete
                      </button>
                    </>
                  )}
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="mt-4 flex justify-center items-center gap-4">
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === 1
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === 1}
          onClick={() => setCurrentPage((prev) => prev - 1)}
        >
          Previous
        </button>
        <span className="text-black">
          Page {currentPage} of {totalPages}
        </span>
        <button
          className={`px-4 py-2 rounded-md ${
            currentPage === totalPages
              ? "bg-gray-400 text-white cursor-not-allowed"
              : "bg-orange-400 text-white hover:bg-orange-500"
          }`}
          disabled={currentPage === totalPages}
          onClick={() => setCurrentPage((prev) => prev + 1)}
        >
          Next
        </button>
      </div>

      {/* Dashboard Button */}
      <div className="mt-6 flex justify-start">
        <Link
          to="/Dashboard"
          className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
        >
          Dashboard
        </Link>
      </div>

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-gray-400 p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold mb-4">Confirm Deletion</h3>
            <p>Are you sure you want to delete "{videoToDelete?.title}"?</p>
            <div className="flex justify-end gap-4 mt-4">
              <button
                onClick={confirmDeleteVideo}
                className="bg-white text-red-600 px-4 py-2 rounded-md"
              >
                Delete
              </button>
              <button
                onClick={cancelDelete}
                className="bg-gray-500 text-white px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VideoManagement;
