import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ForgetPassword = () => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [step, setStep] = useState(1); // Step 1: Enter Email, Step 2: Enter OTP
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();

  // Function to handle OTP sending
  const handleSendOTP = async () => {
    setError("");
    setSuccessMessage("");

    try {
      const response = await fetch("http://localhost:5000/send-otp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();
      if (response.ok) {
        setSuccessMessage("OTP sent successfully! Check your email.");
        setStep(2); // Move to OTP verification step
      } else {
        setError(data.error || "Failed to send OTP.");
      }
    } catch (error) {
      setError("Error sending OTP. Please try again later.");
    }
  };

  // Function to handle OTP verification
  const handleVerifyOTP = async () => {
    setError("");
    setSuccessMessage("");

    try {
      const response = await fetch("http://localhost:5000/verify-otp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ email, otp }),
      });

      const data = await response.json();
      if (response.ok) {
        setSuccessMessage("OTP verified! Redirecting to Reset Password...");
        setTimeout(() => navigate("/reset-password"), 1500); // Redirect after verification
      } else {
        setError(data.error || "Invalid OTP.");
      }
    } catch (error) {
      setError("Error verifying OTP. Please try again later.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-50">
      <div className="bg-white p-8 rounded-lg shadow-md w-96">
        <h2 className="text-3xl font-bold text-gray-800 mb-8 text-center">
          Forgot Password
        </h2>

        {successMessage && (
          <p className="text-green-500 text-sm mb-4">{successMessage}</p>
        )}
        {error && <p className="text-red-500 text-sm mb-4">{error}</p>}

        {step === 1 && (
          <div>
            <label
              htmlFor="email"
              className="block text-sm font-medium text-gray-700 mb-2"
            >
              Enter your email address
            </label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter your email"
              required
            />
            <button
              onClick={handleSendOTP}
              className="w-full bg-orange-400 text-white py-2 px-4 rounded-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring--500 mt-4"
            >
              Send OTP
            </button>
          </div>
        )}

        {step === 2 && (
          <div>
            <label
              htmlFor="otp"
              className="block text-sm font-medium text-gray-700 mb-2"
            >
              Enter the OTP sent to your email
            </label>
            <input
              type="text"
              id="otp"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Enter OTP"
              required
            />
            <button
              onClick={handleVerifyOTP}
              className="w-full bg-orange-400 text-white py-2 px-4 rounded-md hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring--500 mt-4"
            >
              Verify OTP
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default ForgetPassword;
