import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const PlaceManagement = () => {
  const [formData, setFormData] = useState({
    date: '',
    description: '',
    image: '',
    place: ''
  });
  const [places, setPlaces] = useState([]);
  const [search, setSearch] = useState('');
  const [showAddCard, setShowAddCard] = useState(false);
  const [editingIndex, setEditingIndex] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [deleteConfirmation, setDeleteConfirmation] = useState('');
  const [descriptionCardData, setDescriptionCardData] = useState(null);

  // Handle input change
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value
    }));
  };

  // Handle image change
  const handleImageChange = (e) => {
    const { files } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      image: files[0]
    }));
  };

  // Handle add place
  const handleAdd = () => {
    const { date, description, image, place } = formData;
    if (!date || !description || !place || !image) {
      setErrorMessage("Date, Description, Place, and Image are required.");
      setTimeout(() => setErrorMessage(""), 3000);
      return;
    }
    setPlaces([...places, formData]);
    setFormData({ date: "", description: "", image: "", place: "" });
    setShowAddCard(false);
    setSuccessMessage("Place added successfully!");
    setTimeout(() => setSuccessMessage(""), 3000);
  };

  // Handle update place
  const handleUpdate = () => {
    const { date, description, image, place } = formData;
    if (!date || !description || !place || !image) {
      setErrorMessage("Date, Description, Place, and Image are required.");
      setTimeout(() => setErrorMessage(""), 3000);
      return;
    }
    const updatedPlaces = [...places];
    updatedPlaces[editingIndex] = formData;
    setPlaces(updatedPlaces);
    setFormData({ date: "", description: "", image: "", place: "" });
    setEditingIndex(null);
    setShowAddCard(false);
    setSuccessMessage("Place updated successfully!");
    setTimeout(() => setSuccessMessage(""), 3000);
  };

  // Handle delete place
  const handleDelete = (index) => {
    setPlaces(places.filter((_, i) => i !== index));
    setDeleteConfirmation("Place deleted successfully!");
    setTimeout(() => setDeleteConfirmation(""), 3000);
  };

  // Handle view description
  const handleViewDescription = (description) => {
    setDescriptionCardData(description);
  };

  // Handle close description card
  const handleCloseDescriptionCard = () => {
    setDescriptionCardData(null);
  };

  return (
    <div className="flex">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 text-white p-4">
        <h2 className="text-xl font-semibold">Sidebar</h2>
      </div>

      <div className="flex-1 p-6 ml-64">
        <h1 className="text-2xl font-bold mb-4 text-black">Place management</h1>

        {/* Error Message Modal */}
        {errorMessage && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-red-600 p-6 rounded-lg shadow-lg text-white max-w-sm w-full">
              <h3 className="text-lg font-bold mb-4">{errorMessage}</h3>
            </div>
          </div>
        )}

        {/* Success Message Modal */}
        {successMessage && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-green-500 p-6 rounded-lg shadow-lg text-white max-w-sm w-full">
              <h3 className="text-lg font-bold mb-4">{successMessage}</h3>
            </div>
          </div>
        )}

        {/* Delete Confirmation Modal */}
        {deleteConfirmation && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
            <div className="bg-green-500 p-6 rounded-lg shadow-lg text-white max-w-sm w-full">
              <h3 className="text-lg font-bold mb-4">{deleteConfirmation}</h3>
            </div>
          </div>
        )}

        {/* Search Bar */}
        <div className="flex justify-end mb-6">
          <input
            placeholder="Search..."
            type="text"
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            className="border border-gray-400 rounded p-2 w-64"
          />
        </div>

        {/* Add Button */}
        <div className="mb-6 flex justify-end">
          <button
            onClick={() => setShowAddCard((prevState) => !prevState)} // Toggle card visibility
            className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-green-500"
          >
            {showAddCard ? "Cancel" : "Add"}
          </button>
        </div>

        {/* Add Place Form Card */}
        {showAddCard && (
          <div className="mb-6 bg-gray-200 p-6 rounded shadow-md w-full max-w-lg mx-auto">
            <h2 className="text-xl font-semibold mb-4">{editingIndex !== null ? "Edit Place" : "Add Place"}</h2>

            <input
              placeholder="Date"
              type="date"
              name="date"
              value={formData.date}
              onChange={handleInputChange}
              className="border border-gray-400 rounded p-2 w-full mb-4"
            />
            <input
              placeholder="Description"
              type="text"
              name="description"
              value={formData.description}
              onChange={handleInputChange}
              className="border border-gray-400 rounded p-2 w-full mb-4"
            />
            <input
              placeholder="Place"
              type="text"
              name="place"
              value={formData.place}
              onChange={handleInputChange}
              className="border border-gray-400 rounded p-2 w-full mb-4"
            />
            <input
              type="file"
              onChange={handleImageChange}
              className="border border-gray-400 rounded p-2 w-full mb-4"
            />

            <div className="flex justify-end">
              <button
                onClick={editingIndex !== null ? handleUpdate : handleAdd}
                className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
              >
                {editingIndex !== null ? "Update" : "Add"}
              </button>
            </div>
          </div>
        )}

        {/* Places Table */}
        <div className="mb-6">
          <h3 className="text-lg font-semibold mb-4">Places</h3>
          <table className="table-auto w-full bg-gray-100">
            <thead>
              <tr>
                <th className="border px-4 py-2">Date</th>
                <th className="border px-4 py-2">Place</th>
                <th className="border px-4 py-2">Description</th>
                <th className="border px-4 py-2">Actions</th>
              </tr>
            </thead>
            <tbody>
              {places
                .filter((place) => place.place.toLowerCase().includes(search.toLowerCase()))
                .map((place, index) => (
                  <tr key={index}>
                    <td className="border px-4 py-2">{place.date}</td>
                    <td className="border px-4 py-2">{place.place}</td>
                    <td className="border px-4 py-2">
                      <button
                        onClick={() => handleViewDescription(place.description)}
                        className="text-blue-500"
                      >
                        View
                      </button>
                    </td>
                    <td className="border px-4 py-2">
                      <button
                        onClick={() => {
                          setEditingIndex(index);
                          setFormData(place);
                          setShowAddCard(true);
                        }}
                        className="bg-blue-500 text-white py-1 px-3 rounded hover:bg-blue-600"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(index)}
                        className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600 ml-2"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
            </tbody>
          </table>
        </div>

        {/* Description Card */}
        {descriptionCardData && (
          <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center">
            <div className="bg-white p-6 rounded shadow-md w-80">
              <h2 className="text-xl font-semibold mb-4">Description</h2>
              <p>{descriptionCardData}</p>
              <div className="mt-4 text-center">
                <button
                  onClick={handleCloseDescriptionCard}
                  className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Dashboard Button */}
        <div className="mt-6 flex justify-left">
          <Link
            to="/Dashboard"
            className="bg-orange-400 text-white px-4 py-2 rounded-md hover:bg-orange-500 transition duration-300"
          >
            Dashboard
          </Link>
        </div>
      </div>
    </div>
  );
};

export default PlaceManagement;
