import React, { useState } from "react";
import { Link } from "react-router-dom"; // Import Link for navigation

const PlaceManagement = () => {
  const [places, setPlaces] = useState([]);
  const [search, setSearch] = useState("");
  const [formData, setFormData] = useState({
    date: "",
    description: "",
    image: "",
    place: "",
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [showModal, setShowModal] = useState(false); // Show modal state
  const [modalMessage, setModalMessage] = useState(""); // Modal message content
  const [modalType, setModalType] = useState(""); // Modal type (edit, delete)

  // States for delete confirmation
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [videoToDelete, setVideoToDelete] = useState(null); // Store the place to delete

  // States for update confirmation
  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [placeToUpdate, setPlaceToUpdate] = useState(null); // Store the place to update

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Number of items per page

  // State for showing the "Add Place" card
  const [showAddCard, setShowAddCard] = useState(false);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prevData) => ({ ...prevData, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAdd = () => {
    const { date, description, image, place } = formData;
    if (!date || !description || !place || !image) {
      setModalMessage("Date, Description, Place, and Image are required.");
      setModalType("error");
      setShowModal(true);
      return;
    }
    setPlaces([...places, formData]);
    setFormData({ date: "", description: "", image: "", place: "" });
    setShowAddCard(false); // Hide the card after adding
    setModalMessage("Place added successfully!");
    setModalType("success");
    setShowModal(true);
  };

  const handleEdit = (index) => {
    const isConfirmed = window.confirm("Are you sure you want to edit this place?");
    if (isConfirmed) {
      setFormData(places[index]);
      setEditingIndex(index);
      setShowAddCard(true); // Show the card for editing
    }
  };

  const handleUpdate = () => {
    setPlaceToUpdate(formData); // Store the current form data in placeToUpdate
    setShowUpdateModal(true); // Show the update confirmation modal
  };

  const confirmUpdatePlace = () => {
    const updatedPlaces = [...places];
    updatedPlaces[editingIndex] = placeToUpdate; // Update the place
    setPlaces(updatedPlaces);
    setEditingIndex(null);
    setFormData({ date: "", description: "", image: "", place: "" });
    setShowAddCard(false); // Hide the card after update
    setShowUpdateModal(false); // Close the update modal

    setModalMessage("Place updated successfully!");
    setModalType("success");
    setShowModal(true); // Show the modal after successful update
  };

  const cancelUpdate = () => {
    setShowUpdateModal(false); // Close the update confirmation modal
  };

  const handleDelete = (index) => {
    // Show the confirmation modal
    setVideoToDelete(places[index]); // Store the place to be deleted
    setShowDeleteModal(true); // Show the delete modal
  };

  const confirmDeleteVideo = () => {
    const updatedPlaces = places.filter((_, i) => i !== places.indexOf(videoToDelete));
    setPlaces(updatedPlaces);
    setShowDeleteModal(false); // Close the delete confirmation modal

    setModalMessage("Place deleted successfully!");
    setModalType("success"); // Show success message
    setShowModal(true); // Show the modal after successful deletion
  };

  const cancelDelete = () => {
    setShowDeleteModal(false); // Close the delete confirmation modal
  };

  // Filter places based on search
  const filteredPlaces = places.filter(
    ({ date, description, place }) =>
      date.toLowerCase().includes(search.toLowerCase()) ||
      description.toLowerCase().includes(search.toLowerCase()) ||
      place.toLowerCase().includes(search.toLowerCase())
  );

  // Pagination logic
  const totalPages = Math.ceil(filteredPlaces.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredPlaces.slice(indexOfFirstItem, indexOfLastItem);

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4 text-black">Place Management</h1>

      {/* Modal for success/error messages */}
      {showModal && (
        <div
          className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center"
          onClick={() => setShowModal(false)} // Close modal on click outside
        >
          <div
            className="bg-white p-6 rounded shadow-md w-80"
            onClick={(e) => e.stopPropagation()} // Prevent closing when clicking inside
          >
            <p className={`text-center ${modalType === "success" ? "text-green-500" : "text-red-500"}`}>
              {modalMessage}
            </p>
            <div className="mt-4 text-center">
              <button
                onClick={() => setShowModal(false)}
                className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-gray-200 p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold mb-4">Confirm Deletion</h3>
            <p>Are you sure you want to delete "{videoToDelete?.place}"?</p>
            <div className="flex justify-end gap-4 mt-4">
              <button
                onClick={confirmDeleteVideo}
                className="bg-red-600 text-white px-4 py-2 rounded-md"
              >
                Delete
              </button>
              <button
                onClick={cancelDelete}
                className=" text-gray-500 px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Update Confirmation Modal */}
      {showUpdateModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex justify-center items-center">
          <div className="bg-gray-200 p-6 rounded-lg shadow-lg">
            <h3 className="text-lg font-bold mb-4">Confirm Update</h3>
            <p>Are you sure you want to update the details of "{placeToUpdate?.place}"?</p>
            <div className="flex justify-end gap-4 mt-4">
              <button
                onClick={confirmUpdatePlace}
                className="bg-green-600 text-white px-4 py-2 rounded-md"
              >
                Update
              </button>
              <button
                onClick={cancelUpdate}
                className="bg-gray-500 text-white px-4 py-2 rounded-md"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Search Bar */}
      <div className="flex justify-end mb-6">
        <input
          placeholder="Search..."
          type="text"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          className="border border-gray-400 rounded p-2 w-64"
        />
      </div>

      {/* Add Button */}
      <div className="mb-6 flex justify-end">
        <button
          onClick={() => setShowAddCard((prevState) => !prevState)} // Toggle card visibility
          className="bg-green-400 text-white py-2 px-4 rounded hover:bg-green-500"
        >
          {showAddCard ? "Cancel" : "Add Place"}
        </button>
      </div>

      {/* Add Place Form Card */}
      {showAddCard && (
        <div className="mb-6 bg-gray-300 p-6 rounded shadow-md w-full max-w-lg mx-auto">
          <h2 className="text-xl font-semibold mb-4">
            {editingIndex !== null ? "Edit Place" : "Add Place"}
          </h2>

          <input
            placeholder="Date"
            type="date"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 mb-4 w-full"
          />
          <input
            placeholder="Description"
            type="text"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 mb-4 w-full"
          />
          <input
            placeholder="Place"
            type="text"
            name="place"
            value={formData.place}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 mb-4 w-full"
          />
          <input
            type="file"
            name="image"
            onChange={handleImageChange}
            className="mb-4"
          />

          <div className="flex justify-end">
            <button
              onClick={handleUpdate}
              className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
            >
              {editingIndex !== null ? "Confirm Update" : "Add"}
            </button>
          </div>
        </div>
      )}

      {/* Table displaying Places */}
      <table className="table-auto w-full border-collapse border border-gray-400">
        <thead>
          <tr>
            <th className="border border-gray-300 p-2">Date</th>
            <th className="border border-gray-300 p-2">Description</th>
            <th className="border border-gray-300 p-2">Place</th>
            <th className="border border-gray-300 p-2">Image</th>
            <th className="border border-gray-300 p-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {currentItems.map((place, index) => (
            <tr key={index}>
              <td className="border border-gray-300 p-2">{place.date}</td>
              <td className="border border-gray-300 p-2">{place.description}</td>
              <td className="border border-gray-300 p-2">{place.place}</td>
              <td className="border border-gray-300 p-2">
                <img src={place.image} alt={place.place} className="w-16 h-16 object-cover" />
              </td>
              <td className="border border-gray-300 p-2">
                <button
                  onClick={() => handleEdit(index)}
                  className="bg-blue-500 text-white py-1 px-2 rounded hover:bg-blue-600"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(index)}
                  className="bg-red-500 text-white py-1 px-2 rounded hover:bg-red-600 ml-2"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="flex justify-center mt-4">
        <button
          onClick={() => setCurrentPage((prevPage) => Math.max(prevPage - 1, 1))}
          className="bg-gray-300 text-black py-2 px-4 rounded-l"
        >
          Prev
        </button>
        <span className="px-4 py-2">{currentPage}</span>
        <button
          onClick={() => setCurrentPage((prevPage) => Math.min(prevPage + 1, totalPages))}
          className="bg-gray-300 text-black py-2 px-4 rounded-r"
        >
          Next
        </button>
      </div>
    </div>
  );
};

export default PlaceManagement;
