import React, { useState } from "react";

const PlaceManagement = () => {
  const [places, setPlaces] = useState([]);
  const [search, setSearch] = useState("");
  const [formData, setFormData] = useState({
    date: "",
    description: "",
    image: "",
    place: "",
    imageSource: "url", // Default to URL
  });
  const [editingIndex, setEditingIndex] = useState(null);
  const [showAddOptions, setShowAddOptions] = useState(false);
  const [confirmationMessage, setConfirmationMessage] = useState({
    show: false,
    text: "",
    type: "", // 'update' or 'delete'
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setFormData((prevData) => ({ ...prevData, image: reader.result }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleAdd = () => {
    const { date, description, image, place } = formData;
    if (!date || !description || !place) {
      alert("Date, Description, and Place are required.");
      return;
    }
    setPlaces([...places, formData]);
    setFormData({ date: "", description: "", image: "", place: "", imageSource: "url" });
    setShowAddOptions(false);
  };

  const handleEdit = (index) => {
    setFormData(places[index]);
    setEditingIndex(index);
    setShowAddOptions(true); // Show the form for editing
  };

  const handleUpdate = () => {
    if (editingIndex !== null) {
      const updatedPlaces = [...places];
      updatedPlaces[editingIndex] = formData;
      setPlaces(updatedPlaces);
      setEditingIndex(null);
      setFormData({ date: "", description: "", image: "", place: "", imageSource: "url" });
      setShowAddOptions(false);

      // Show update confirmation message
      setConfirmationMessage({
        show: true,
        text: "Place updated successfully!",
        type: "update",
      });
      setTimeout(() => {
        setConfirmationMessage({ show: false, text: "", type: "" });
      }, 3000); // Hide after 3 seconds
    }
  };

  const handleDelete = (index) => {
    const updatedPlaces = places.filter((_, i) => i !== index);
    setPlaces(updatedPlaces);

    // Show delete confirmation message
    setConfirmationMessage({
      show: true,
      text: "Place deleted successfully!",
      type: "delete",
    });
    setTimeout(() => {
      setConfirmationMessage({ show: false, text: "", type: "" });
    }, 3000); // Hide after 3 seconds
  };

  const filteredPlaces = places.filter(
    ({ date, description, place }) =>
      date.toLowerCase().includes(search.toLowerCase()) ||
      description.toLowerCase().includes(search.toLowerCase()) ||
      place.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4 text-black">Place Management</h1>

      {/* Confirmation Message */}
      {confirmationMessage.show && (
        <div
          className={`mb-4 p-3 rounded ${
            confirmationMessage.type === "update"
              ? "bg-green-400"
              : confirmationMessage.type === "delete"
              ? "bg-red-500"
              : ""
          } text-white`}
        >
          {confirmationMessage.text}
        </div>
      )}

      <div className="flex justify-between items-center mb-6">
        {/* Add Button */}
        <button
          onClick={() => {
            setShowAddOptions(!showAddOptions);
            setFormData({ date: "", description: "", image: "", place: "", imageSource: "url" });
            setEditingIndex(null); // Reset editing index
          }}
          className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500"
        >
          {showAddOptions ? "Close" : "Add Place"}
        </button>

        {/* Search Bar */}
        {formData.imageSource !== "file" && (
          <div className="relative">
            <input
              placeholder="Search..."
              type="text"
              value={search}
              onChange={(e) => setSearch(e.target.value)}
              className="border border-gray-400 rounded p-2 w-64"
            />
          </div>
        )}
      </div>

      {/* Add/Edit Form */}
      {showAddOptions && (
        <div className="mb-6 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <input
            placeholder="Date"
            type="date"
            name="date"
            value={formData.date}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full"
          />
          <input
            placeholder="Description"
            type="text"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full"
          />
          <input
            placeholder="Place"
            type="text"
            name="place"
            value={formData.place}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full"
          />

          {/* Image Source Option */}
          <select
            name="imageSource"
            value={formData.imageSource}
            onChange={handleInputChange}
            className="border border-gray-400 rounded p-2 w-full"
          >
            <option value="url">Image URL</option>
            <option value="file">Upload File</option>
          </select>

          {/* Image URL Input */}
          {formData.imageSource === "url" && (
            <input
              placeholder="Image URL"
              type="text"
              name="image"
              value={formData.image}
              onChange={handleInputChange}
              className="border border-gray-400 rounded p-2 w-full"
            />
          )}

          {/* Image File Input */}
          {formData.imageSource === "file" && (
            <input
              type="file"
              onChange={handleImageChange}
              className="border border-gray-400 rounded p-2 w-full"
            />
          )}

          <button
            onClick={editingIndex !== null ? handleUpdate : handleAdd}
            className="bg-orange-400 text-white py-2 px-4 rounded hover:bg-orange-500 col-span-2"
          >
            {editingIndex !== null ? "Update" : "Add"}
          </button>
        </div>
      )}

      {/* Data Table */}
      <table className="table-auto w-full border-collapse border border-gray-400">
        <thead>
          <tr>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Date</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Description</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Place</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Image</th>
            <th className="px-4 py-2 border-b bg-orange-400 text-white text-left">Actions</th>
          </tr>
        </thead>
        <tbody>
          {filteredPlaces.map((place, index) => (
            <tr key={index} className="border-b hover:bg-gray-50">
              <td className="px-4 py-2 text-black">{place.date}</td>
              <td className="px-4 py-2 text-black">{place.description}</td>
              <td className="px-4 py-2 text-black">{place.place}</td>
              <td className="px-4 py-2">
                {place.image && (
                  <img
                    src={place.image}
                    alt={place.description}
                    className="w-16 h-16 object-cover rounded"
                  />
                )}
              </td>
              <td className="px-4 py-2">
                <button
                  onClick={() => handleEdit(index)}
                  className="bg-blue-400 text-white py-1 px-3 rounded hover:bg-blue-500 mr-2"
                >
                  Edit
                </button>
                <button
                  onClick={() => handleDelete(index)}
                  className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600"
                >
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PlaceManagement;
